import * as api from "@/api/project/srm/priceManage/priceConfirmList"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
// import * as api from "@/api/project/srm/contractManage/paymentMethod"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import dialogWlxzbycglx from '@/components/Dialog/dialogWlxzbycglx.vue'//物料选择
import addMain from './dialog/addMain.vue'//物料选择
export default {
    name: "priceConfirmList",
    components: { Pagination, caiGou, dialogGysxz, dialogWlxzbycglx, addMain },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: false,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            subList: [],
            total: 0,
            totalSub: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: [0, 1], //表格索引
            rangeDate: null
        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pc6: state => state.dict.dt_srm_pc6,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pcb: state => state.dict.dt_srm_pcb
        })
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.createTimeBegin = value[0]
                this.listQuery.createTimeEnd = value[1]
            } else {
                this.listQuery.createTimeBegin = undefined
                this.listQuery.createTimeEnd = undefined
            }
        }
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pc6', 'dt_srm_pdt', 'dt_srm_pcb'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    filters: {
        stateFilter: function (a) {
            if (a) {
                if (a == '0') {
                    return '不通过'
                }
                if (a == '1') {
                    return '通过'
                }
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() / 2
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        showWl () {
            this.$refs.wlxz.dialogVisible = true
            this.$refs.wlxz.getList()
        },
        confirmWlxz (data) {
            if (data) {
                this.$set(this.listQuery, 'materialCode', data[0].materialCode)
                this.$set(this.listQuery, 'materialName', data[0].materialName)
            }
        },
        refresh () {
            this.getList()
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pc6, this.dt_srm_pdt]
                res.data.list = this.$myFun.codeToName(res.data.list, options, ['state', 'documentType', 'settlementMethod'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        getsubList () {
            api.queryRecordsItems(this.selectlistRow.confirmationNo).then(res => {
                let options = [this.dt_srm_pcb]
                res.data = this.$myFun.codeToName(res.data, options, ['dataSource'])
                this.subList = res.data
                this.totalSub = res.data.length
            })
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.initData()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //发布按钮单击事件
        handlePublish () {
            if (this.selectlistRow) {
                this.$confirm("是否发布勾选的报价通知？发布后将向相关供应商推送报价通知", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        method: 'publish',
                        rowIds: [this.selectlistRow.rowId]
                    }
                    api.submitRecords(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '发布成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //提交按钮单击事件
        handleSubmit () {
            if (this.selectlistRow) {
                this.$confirm("是否发布勾选的报价通知？发布后将向相关供应商推送报价通知", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        method: 'commit',
                        rowIds: [this.selectlistRow.rowId]
                    }
                    api.submitRecords(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '提交成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //撤回按钮单击事件
        handleRevork () {
            if (this.selectlistRow) {
                this.$confirm("是否驳回勾选的报价通知？", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        method: 'reject',
                        rowIds: [this.selectlistRow.rowId]
                    }
                    api.submitRecords(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '驳回成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //改按钮单击事件
        handleUpdate (index, row) {
            this.$refs.addMain.temp = { ...row }
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.getList(row)
            this.$refs.addMain.dialogVisible = true
        },
        //删按钮单击事件
        handleDelete (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.subList = []
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getsubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
