import * as api from "@/api/project/srm/materialClassify/materialMaintain"
import Pagination from "@/components/Pagination"
import classifyCode from "./classifyCode"
import { mapState } from "vuex"
export default {
    name: "delivery_Publish",
    components: { Pagination, classifyCode },
    data () {
        return {
            dialoggysVisible: false,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            totalSub: 0,
            selectedrow: null,//主表选中行
            codeValue: undefined,
            dialogVisible: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                materielClass: undefined
            },
            listQuerySub: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                materielClass: undefined
            },
            orgCode: null,
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pa1: state => state.dict.dt_srm_pa1,
            dt_srm_pa6: state => state.dict.dt_srm_pa6,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pa1', 'dt_srm_pa6'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {

        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() / 3
        },

        //显示主表
        getList () {
            api.queryRecordSupplier(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pa1, this.dt_srm_pa6]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'supplierType', 'grade'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //显示从表
        getSubList () {
            api.queryRecordSupplier(this.listQuerySub).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pa1, this.dt_srm_pa6]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'supplierType', 'grade'])
                this.listSub = res.data.list
                this.totalSub = res.data.pages.count
            })
        },
        handleClassifyCode (val) {
            this.codeValue = val
            this.$refs.classify.dialogCodeVisible = true
        },
        listSelected (val) {
            if (this.codeValue === 'main') {
                this.listQuery.materielClass = val[0].classCode
                this.listQuery.className = val[0].className
            }
            if (this.codeValue === 'sub') {
                this.listQuerySub.materielClass = val[0].classCode
                this.listQuerySub.className = val[0].className
            }
            this.$refs.classify.dialogCodeVisible = false
        },
        handleTransfer () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                if (this.listQuerySub.materielClass) {
                    let dto = {
                        materielClass: this.listQuery.materielClass,
                        orgCode: this.orgCode,
                        supplierCodes: [],
                        transferMaterielClass: this.listQuerySub.materielClass,
                        transferMaterielClassName: this.listQuerySub.className
                    }
                    this.selectlistRow.forEach((val, index) => {
                        dto.supplierCodes[index] = val.supplierCode
                    })
                    api.transfer(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '迁移成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                        this.getSubList()
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择迁移后的分类编码',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //删除
        handleDelete () {

        },
        //查询
        handleQuery () {
            this.getList()
        },
        handleQuerySub () {
            this.getSubList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
            // if (val.length > 1) {
            //     this.$refs.tb.clearSelection()
            //     this.$refs.tb.toggleRowSelection(val.pop())
            // }
            // this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
