// import * as api from "@/api/project/srm/dataBase/specialDistinction"
import * as api from '@/api/project/eas/el/equipmentRequisition'
import { DICT_CODE } from '@/utils/constant'
import { mapState } from 'vuex'
import vendorMainSelect from '@/components/Select/vendorMainSelect.vue' //厂家
import personSelect from '@/components/Select/personSelect.vue'
export default {
    components: { vendorMainSelect, personSelect },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            temp: {
                rowId: undefined,
                manufacturersName: undefined,
                accountQuantity: 0
            },
            dialogStatus: '',
            dialogVisible: false,
            rules: {
                purchaseOrg: [
                    {
                        required: true,
                        message: '采购组织不能为空',
                        trigger: 'blur'
                    }
                ],
                distinctionName: [
                    {
                        required: true,
                        message: '区分名称不能为空',
                        trigger: 'blur'
                    }
                ]
            },
            dialogVisibleVendorMain: false,
            dialogVisiblePerson: false,
            parentRows: [],
            parentRowsperson: []
        }
    },
    computed: {
        ...mapState({
            dt_srm_pbb: state => state.eamDict.dt_srm_pbb
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', ['dt_srm_pbb'])
    },
    methods: {
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        handleEquipment () {
            this.dialogVisibleVendorMain = true
        },
        handleVendorMain (val) {
            this.parentRows = val
        },
        confirmVendorMain () {
            if (this.parentRows) {
                this.$set(
                    this.temp,
                    'manufacturersCode',
                    this.parentRows.manufacturersCode
                )
                this.$set(
                    this.temp,
                    'manufacturersName',
                    this.parentRows.manufacturersName
                )
                this.dialogVisibleVendorMain = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //请购人选择
        handlePerson () {
            this.dialogVisiblePerson = true
        },
        //请购人选择
        person (val) {
            this.parentRowsperson = val
        },
        //请购人确定
        confirmPerson () {
            if (this.parentRowsperson && this.parentRowsperson.length === 1) {
                this.$set(
                    this.temp,
                    'handltor',
                    this.parentRowsperson[0].employeeCode
                )
                this.$set(
                    this.temp,
                    'handltorName',
                    this.parentRowsperson[0].employeeName
                )
                this.dialogVisiblePerson = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        createData () {
            let dto = {
                accountQuantity: 0,
                arrivalQuantity: this.temp.arrivalQuantity,
                category: this.temp.category,
                deviceName: this.temp.deviceName,
                manufacturersCode: this.temp.manufacturersCode,
                modelNumber: this.temp.modelNumber,
                referencePrice: this.temp.referencePrice,
                requisitionsQuantity: this.temp.requisitionsQuantity,
                supplierCode: this.temp.supplierCode,
                typeCode: this.temp.typeCode,
                arrivalTime: this.temp.arrivalTime,
                handltorName: this.temp.handltorName,
                handltor: this.temp.handltor
            }
            api.arrival(
                this.temp.requisitionsOrderCode,
                this.temp.rowNo,
                dto
            ).then(() => {
                this.$message({
                    title: '成功',
                    message: '到货成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        },
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                distinctionCode: undefined,
                distinctionName: undefined,
                remark: undefined
            }
        }
    }
}
