import * as api from '@/api/project/eas/database/groups'
export default {
    data() {
        return {
            dialogVisible: false,
            dialogStatus: '',
            temp: {}
        }
    },
    methods: {
        //新增确定按钮单击事件
        createData() {
            api.addRecord(this.temp).then(() => {
                this.dialogVisible = false
                this.$emit('dialogVisible', this.dialogVisible)
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
            })
        },
        //编辑确定按钮单击事件
        updateData() {
            api.updateRecord(this.temp.teamCode, this.temp).then(() => {
                this.dialogVisible = false
                this.$emit('dialogVisible', this.dialogVisible)
                this.$message({
                    title: '成功',
                    message: '更新成功',
                    type: 'success',
                    duration: 2000
                })
            })
        },
        resetTemp() {
            this.temp = {
                orgName: undefined,
                orgCode: undefined,
                teamCode: undefined,
                teamName: undefined,
                quarams4: undefined
            }
        }
    }
}
