import *  as api from '@/api/project/eas/el/equipmentArchives'
import Pagination from '@/components/Pagination'
import eqmtSelect from '../../components/eqmtSelect'

export default {
    name: 'dispatchOrderManagement',
    components: { Pagination, eqmtSelect },
    props: ['deviceCode', 'typeCode', 'selectlist'],
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            childDeviceCode: [],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,

            },
            listSelect: [],
            subList: [],
            parentsRow: [],
            importDialogVisible: false,
            tableKey: [0, 1],
            ids: [],
        }
    },
    created () {
    },
    watch: {
        deviceCode (val) {
            if (val) {
                this.getList()
            }
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {

        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        init (val) {
            this.subList = val
            this.getList()

        },
        getList () {
            if (this.deviceCode) {
                api.querySubDevice(this.deviceCode).then((res) => {
                    this.list = res.data
                })
            }

        },
        handleCreate () {
            if (this.selectlist) {

                let arr = this.list.map(i => {
                    return {
                        deviceCode: i.childDeviceCode
                    }
                })
                arr.push({ deviceCode: this.deviceCode })
                this.childDeviceCode = arr
                this.importDialogVisible = true
                setTimeout(() => {
                    this.$refs.eqmtSelect.getList()
                }, 0)
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择设备编码',
                    type: 'warning'
                })
            }
        },
        update () {
            let childDeviceCodes = this.$refs.eqmtSelect.currentSelect.map(i => {
                return i.deviceCode
            })
            api.addSubDevice(this.deviceCode, childDeviceCodes).then(() => {
                this.getList()
                this.importDialogVisible = false
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
            })
        },
        handleDelete () {
            //删除列表
            this.selectlistRow.forEach((val, index) => {
                this.ids[index] = val.rowId
            })
            if (this.selectlistRow) {
                this.$confirm('此操作将永久删除子设备, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        api.deleteDevice(this.ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },
    }
}
