import Pagination from '@/components/Pagination' // Secondary package based on el-pagination
import * as api from '@/api/project/mes/dataBase/processProcess/processProcess'
import processProcessSelect from '@/components/Select/processProcessSelect'
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'stationEquipment',
    components: { Pagination, processProcessSelect },
    data() {
        return {
            theight: 0, //表格高度
            workCenterData: [],
            select_a: [],
            select_b: [],
            list_a: [],
            list_b: [],
            total_a: 0,
            total_b: 0,
            listQuery_a: {
                currentPage: 1,
                pageSize: 15,
                stationCode: undefined,
                stationName: undefined
            },
            listQuery_b: {
                currentPage: 1,
                pageSize: 15,
                stepCode: undefined,
                stepName: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            temp: []
        }
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_device_type: state => state.mesDict.dt_device_type,
            dt_assemble_line: state => state.mesDict.dt_assemble_line,
            dt_manufacture_line: state => state.mesDict.dt_manufacture_line
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', [
            'dt_device_type',
            'dt_assemble_line',
            'dt_manufacture_line'
        ])
        this.setTableHeight()
        this.getWorkCenterData()
        this.getList_a()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight() + 20
            this.stheight = this.theight - 6
        },
        getWorkCenterData() {
            api.getWorkCenterList().then(res => {
                this.workCenterData = res.data
            })
        },
        //单击行绑定点击事件
        rowClick_a(val) {
            this.$refs.tb_a.toggleRowSelection(val, 'selected')
        },
        rowClick_b(val) {
            this.$refs.tb_b.toggleRowSelection(val, 'selected')
        },
        // 查询工位列表
        getList_a() {
            this.list_b = []
            api.getStationList(this.listQuery_a).then(res => {
                this.list_a = res.data.list
                this.total_a = res.data.pages.count
            })
        },
        // 查询工位对应设备
        getList_b() {
            api.getProcessList(
                this.listQuery_b,
                this.select_a[0].stationCode
            ).then(res => {
                this.list_b = res.data.list
                this.total_b = res.data.pages.count
            })
        },
        selectRow_a(val) {
            this.select_a = val
            if (val.length == 0) {
                this.select_a = []
                this.list_b = []
                return false
            }
            if (val.length > 1) {
                this.$refs.tb_a.clearSelection()
                this.$refs.tb_a.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb_a.setCurrentRow(this.select_a[0])
            if (this.select_a.length) {
                this.getList_b()
            }
        },
        selectRow_b(val) {
            this.select_b = val
            if (val.length > 1) {
                this.$refs.tb_b.clearSelection()
                this.$refs.tb_b.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb_b.setCurrentRow(this.select_b[0])
        },
        handleCreate() {
            if (Array.isArray(this.select_a) && this.select_a[0]) {
                this.resetTemp()
                this.getList_b()
                //新增按钮单击事件方法
                this.dialogStatus = 'create'
                this.dialogFormVisible = true
                setTimeout(() => {
                    this.$refs.stationEquipmentSelect.getListSelected()
                    this.$refs.stationEquipmentSelect.getEquipmentData()
                }, 0)
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择工位',
                    type: 'warning'
                })
            }
        },
        handleDelete() {
            api.deleteProcessList(
                this.select_b[0],
                this.select_a[0].stationCode
            ).then(res => {
                if (res.status == 204) {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_b()
                }
            })
        },
        resetTemp() {
            this.temp = []
        },
        handleClose() {
            this.$refs.stationEquipmentSelect.$refs.tb_bx.clearSelection()
            this.getList_b()
        },
        // 添加工位设备
        update() {
            api.patchProcessList(
                this.$refs.stationEquipmentSelect.listSelected,
                this.select_a[0].stationCode
            ).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '新增成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_b()
                    this.dialogFormVisible = false
                }
            })
        }
    }
}
