import *  as api from '@/api/project/eas/el/equipmentSpotCheck'
import Pagination from '@/components/Pagination'

export default {
    name: 'equipmentSpotCheck',
    components: { Pagination },
    props: {
        // 父组件中已选记录集合
        currentSelect: {
            type: Array,
            default: () => {
                return []
            }
        },
        cDeviceCode: {
            type: String,
            default: () => {
                return undefined
            }
        }
    },
    data () {
        return {
            list: null,
            total: 0,
            options: [],
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                deviceCodes: []
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false,
            newArr: []
        }
    },
    mounted () {
        this.getTypeCode()
    },
    methods: {
        //查设备类型
        getTypeCode () {
            api.queryTypeCode().then(res => {
                this.options = res.data
            })
        },

        handleQuery () {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.listSelected = []

        },
        selectList (val) {
            this.selectListRow = val
            this.$emit("change", this.selectListRow)
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.deviceCode === row.deviceCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.deviceCode === items.deviceCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.deviceCode !== row.deviceCode
                })
            }
        },
        selectAll (val) {
            if (val.length == 0) {
                this.listSelected = []
                this.$refs.tb_bx.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.deviceCode === items.deviceCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 父级工位表格展示已选，modal已选勾选
        getListSelected () {
            this.parentList = this.$parent.$parent.list
        },
        getData () {
            this.$refs.tb_bx.clearSelection()
            this.listSelected = []
        },
        // 在备选中把已选打钩
        getEquipmentData () {
            this.newArr.push(this.cDeviceCode)
            for (var i = 0; i < this.currentSelect.length; i++) {
                this.newArr.push(this.currentSelect[i].childDeviceCode)
            }
            this.listQuery.deviceCodes = this.newArr.toString()
            api.queryMainlist(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                setTimeout(() => {
                    this.getData()
                }, 0)
            })
        }
    }
}
