import request from '@/utils/request'

export default {
    name: 'warehouse-select',
    props: {
        orgCode: {
            required: true,
            type: String,
            default: ''
        },
        warehouseCode: {
            type: String,
            default: ''
        },
        placeholder: {
            type: String,
            default: ''
        },
        disabled: {
            type: Boolean,
            default: false
        }
    },
    model: {
        prop: 'warehouseCode',
        event: 'change'
    },
    data () {
        return {
            options: [],
            selectValue: this.warehouseCode
        }
    },
    watch: {
        warehouseCode: {
            handler: function () {
                this.selectValue = this.warehouseCode
            },
            immediate: true
        },
        orgCode: {
            handler: function (val) {
                if (val) {
                    this.getOptions()
                } else {
                    this.options = []
                }
                // if (oldVal) {
                //     this.selectValue = undefined
                //     this.$emit('change', undefined)
                // }
            },
            immediate: true
        }
    },
    methods: {
        getOptions () {
            return request({
                url: '/base-modeling/warehouses',
                method: 'get',
                params: {
                    orgCode: this.orgCode
                }
            }).then(res => {
                this.options = res.data
            })
        },
        changeSelect (val) {
            this.$emit('change', val)
            this.$emit('input', val)
            let name = ''
            let org = this.options
                .filter(v => v.warehouseCode == val)
                .find(value => value)
            if (org) {
                name = org.warehouseName
            }

            this.$emit('selectName', name)
        }
    }
}
