import Pagination from '@/components/Pagination'
import { DICT_CODE, TWO_STATE_OPTIONS } from '@/utils/constant'
// import { mapState } from 'vuex'

export default {
    name: 'ghxl',
    components: { Pagination, caiGou },
    props: ['unMutiple', 'title'],
    data () {
        return {
            list: [
                { rowId: '1', quoteNoteLetterNo: '否', supplierCode: '110108', supplierName: '北京德尔福万源发动机管理系统有限公司', mapNo: 'YL0000001', specialDistinction: '01', materielCode: 'YL0000001', projectNo: 'LRRCR', materialClassificationName: '凸轮轴', publishTime: '2018/5/13' },
                { rowId: '2', quoteNoteLetterNo: '否', supplierCode: '110108', supplierName: '北京德尔福万源发动机管理系统有限公司', mapNo: 'LRXZMC2018052202', specialDistinction: '02', materielCode: 'LRXZMC2018052202', projectNo: 'LRRCR', materialClassificationName: '机油冷却器', publishTime: '2018/5/22' }
            ],
            listSelected: [],
            dialogVisible: false,
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                orgCode: null,
                materielCode: undefined,
                materielName: undefined,
                supplierCode: undefined,
                supplierName: undefined
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            TWO_STATE_OPTIONS: TWO_STATE_OPTIONS,
            filterConfig: [
                {
                    type: 'selectcaiGou',
                    label: '采购组织',
                    prop: 'orgCode',
                    width: '',
                    copmNum: 0,
                    class: 'filter-item'
                },
                {
                    type: 'commonInput',
                    label: '零件图号',
                    prop: 'materielCode',
                    width: '',
                    class: 'filter-item ml_5'
                },
                {
                    type: 'commonInput',
                    label: '物料名称',
                    prop: 'materielName',
                    width: '',
                    class: 'filter-item ml_5'
                },
                {
                    type: 'commonInput',
                    label: '供应商编码',
                    prop: 'supplierCode',
                    width: '',
                    class: 'filter-item ml_5'
                },
                {
                    type: 'commonInput',
                    label: '供应商名称',
                    prop: 'supplierName',
                    width: '',
                    class: 'filter-item ml_5'
                }
            ],
            tableConfig: [
                { label: '序号', prop: 'rowId', align: 'center', width: 'auto' },
                { label: '是否有报', prop: 'quoteNoteLetterNo', align: 'center', width: 'auto' },
                { label: '供应商编码', prop: 'supplierCode', align: 'center', width: 'auto' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: 'auto' },
                { label: '零件图号', prop: 'mapNo', align: 'center', width: 'auto' },
                { label: '特殊区分', prop: 'specialDistinction', align: 'center', width: 'auto' },
                { label: '物料名称', prop: 'materielCode', align: 'center', width: 'auto' },
                { label: '项目名称', prop: 'projectNo', align: 'center', width: 'auto' },
                { label: '物料分类', prop: 'materialClassificationName', align: 'center', width: 'auto' },
                { label: '线路发布时间', prop: 'publishTime', align: 'center', width: 'auto' }
            ]
        }
    },
    computed: {
        // ...mapState({
        //     dt_materiel_classify: state => state.dict.dt_materiel_classify,
        //     dt_materiel_group: state => state.dict.dt_materiel_group,
        //     dt_materiel_type: state => state.dict.dt_materiel_type,
        // })
    },
    mounted () {
        this.getList()
        // this.$store.dispatch('dict/getDicData', ['dt_materiel_classify', 'dt_materiel_group', 'dt_materiel_type'])
    },
    methods: {
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            // queryRecords(this.listQuery).then(res => {
            //     res = res.data
            //     this.list = res.list
            //     this.total = res.pages.count
            if (this.listSelected) {
                this.listSelected.forEach(items => {
                    this.list.forEach((item, index) => {
                        if (item.materielCode == items.materielCode) {
                            this.$nextTick(function () {
                                this.checked(index)
                            })
                        }
                    })
                })
            }
            // })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(
                            val[val.length - 1],
                            'selected'
                        )
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach(item => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        },
        //选择确认
        handleConfirm () {
            this.$emit('handleDialogClick', this.listSelected)
            this.dialogVisible = false
        }
    }
}
