import Pagination from "@/components/Pagination" // 分页组件
import * as api from '@/api/project/wms/stokMove/indicateAllocation'
import { DT_ORG_TYPE } from "@/utils/constant"
import orgSelect from "@/components/Select/orgSelect.vue"
import khxz from "@/components/Select/khxz" //供应商选择
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import qrcode from "@/components/QRCode"
import { mapState } from "vuex"
import warehouseSelect from "@/components/Select/warehouse-select.vue" //下拉组件


export default {
    name: "indicateAllocation",
    components: { Pagination, orgSelect, khxz, AuthoType, qrcode, warehouseSelect },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            list: null,
            list1: null,
            list2: null,
            list3: null,
            areaList: null,
            locatorList: [],
            toareaList: null,
            tolocatorList: [],
            areaCode: null,
            value1: "",
            value2: "",
            value3: "",
            valuedate: "",
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            activeName2: "first",
            queryState: ['ADD', 'PART'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                pushTimeStart: undefined,
                executionStatus: undefined,
                businessType: undefined,
                orgCode: undefined,
                warehouseCode: undefined,
                method: undefined,
                state: 'instruction20',
                accountDate: this.timeFormat()
            },
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuery1: {
                currentPage: 1,
                pageSize: 10
            },
            listQuery2: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                executionStatus: undefined,
                pushOrderCode: undefined
            },
            dta: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined,
                businessType: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                receiveOrderItemDTOs: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            isEdit: true,
            locateWarehouseCode: null,
            DT_ORG_TYPE: DT_ORG_TYPE,
            tableKey: [0, 1, 2, 3],
            downloadLoading: false,
            dialogPrintVisible: false,
            printData: {},//打印数据
        }
    },
    computed: {
        ...mapState({
            dt_work_center_workshop: state =>
                state.businessComponent.dt_work_center_workshop,
            dt_business_type_screenGroupNum_db: state =>
                state.businessComponent.dt_business_type_screenGroupNum_db
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("dict/getDicData", [
            "dt_materiel_type",
            "dt_materiel_classify"
        ])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_work_center_workshop",
            "dt_business_type_screenGroupNum",
            "dt_business_type_screenGroupNum_db"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 110
            this.pheight = this.$myFun.getSingleTbHeight()
            this.sheight = this.$myFun.getSingleTbHeight() - 40
        },
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getList2()
            } else {
                this.getList()
            }
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleQuery1 () {
            this.listQueryProofMain.currentPage = 1
            let stateList = this.dt_business_type_screenGroupNum_db.options.filter(v => {
                return v.wareRuleCode == this.listQueryProofMain.businessType
            })
            this.getList2(stateList)
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        getList () { //查询出库登记主表
            this.listQuery.executionStatus = this.queryState.join(",")
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                if (this.total == 0) {
                    this.list1 = []
                }
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getList1 () {//查询出库登记从表
            // this.listQueryItems.executionStatus = this.queryState.join(",");
            this.listQueryItems.allocNumber = this.selectlistRow.allocNumber
            api.getRecord(this.listQueryItems).then(res => {
                res = res.data
                res.list.forEach((item, index) => {
                    res.list[index].curQuantity = 0
                    res.list[index].invalidQuantity = res.list[index].allocateQuantity - res.list[index].allocatedQuantity
                })
                this.list1 = res.list
                for (let i = 0; i < this.list1.length; i++) {
                    this.locatorList.push([])
                }
                api.getArea(this.locateWarehouseCode).then(resa => {
                    resa = resa.data
                    this.areaList = resa
                })
                api.getArea(this.selectlistRow.toWarehouseCode).then(resa => {
                    resa = resa.data
                    this.toareaList = resa
                })
            })
        },

        getList2 (val) {//查询出库凭证主表
            // this.listQuery2.businessType='0526'
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.list3 = []
            if (val && val[0].accountDirection == 0) {
                api.queryRecordscb(this.listQueryProofMain).then(res => {
                    res = res.data
                    this.list2 = res.list
                    // let b = res.list.map(v => {
                    // v.pushOrderCode = v.outOrderNumber;
                    // return v;
                    // });
                    // this.list2.pushOrderCode = b;
                    this.total2 = res.pages.count
                })
            } else {
                api.queryRecordsIn(this.listQueryProofMain).then(res => {
                    res = res.data
                    this.list2 = res.list.map(v => {
                        v.outOrderNumber = v.receiveOrderNumber
                        return v
                    })
                    this.total2 = res.pages.count
                })
            }

        },

        getList3 (val) {//查询出库凭证从表
            if (val && val[0].accountDirection == 0) {
                this.listQueryProofSub.outOrderNumber = this.selectlistRowout.outOrderNumber
                api.queryRecordsmx(this.listQueryProofSub).then(res => {
                    res = res.data
                    this.list3 = res.list
                    this.total3 = res.pages.count
                })
            } else {
                this.listQueryProofSub.receiveOrderNumber = this.selectlistRowout.receiveOrderNumber
                api.queryRecordsInItems(this.listQueryProofSub).then(res => {
                    this.list3 = res.data.list.map(v => {
                        v.outStockNum = v.receiveQuantity
                        v.outOrderNumber = v.receiveOrderNumber
                        v.businessTypeName = this.selectlistRowout.businessTypeName
                        return v
                    })
                    this.total3 = res.data.pages.count
                })
            }
        },
        savecheck () {
            let result = true
            let b = this.list1.map(v => {
                //   v.outStockNum= v.receiveOrderNum
                v.outStockNum = v.curQuantity
                v.sourceDetailNo = v.rowNo
                return v
            })
            b = this.list1.filter(v => {
                return v.outStockNum !== 0 && v.outStockNum !== ""
            })
            b.forEach((v) => {
                if (!v.wareAreaCode || !v.wareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    })
                    result = false
                }
            })
            // this.save=b
            if (result) {
                return b
            } else {
                return false
            }

        },
        //保存
        handleSave () {
            if (this.listQuery.businessType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow) {
                    // if(v.wareAreaCode&&v.wareLocationCode)
                    let a = this.savecheck()
                    if (a) {
                        let stateList = this.dt_business_type_screenGroupNum_db.options.filter(v => {
                            return v.wareRuleCode == this.selectlistRow.businessType
                        })
                        this.dto = this.selectlistRow
                        this.dto.businessType = this.selectlistRow.businessType
                        this.dto.accountDate = this.listQuery.accountDate
                        this.dto.sourceOrderCode = this.selectlistRow.allocNumber
                        this.dto.sourceOrderNumber = this.selectlistRow.allocNumber
                        this.dto.customerCode = '*'
                        if (stateList[0].accountDirection == 1) {
                            this.dto.preSourceOrderNumber = this.selectlistRow.allocNumber
                            this.dto.receiveOrderItemDTOs = a.map(v => {
                                v.receiveQuantity = v.curQuantity
                                v.sourceOrderNumber = v.allocNumber
                                v.preSourceOrderNumber = v.allocNumber
                                v.sourceRowNo = v.rowNo
                                return v
                            })
                            api.SaveIn(this.dto).then(() => {
                                this.$notify({
                                    title: "成功",
                                    message: "保存成功",
                                    type: "success",
                                    duration: 2000
                                })
                                this.getList1()
                            })
                        } else {
                            this.dto.outOrderPlans = a
                            api.Save(this.dto).then(() => {
                                this.$notify({
                                    title: "成功",
                                    message: "保存成功",
                                    type: "success",
                                    duration: 2000
                                })
                                this.getList1()
                            })
                        }
                    }
                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                })
            }
        },

        //复制
        handleCopy () {
            if (this.selectlistRow3) {
                this.list1.map((item, index) => {
                    this.selectlistRow3.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.list1[index].curQuantity = item.invalidQuantity
                            this.$set(this.list1, index, this.list1[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowout) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowout
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })

            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '打印次数', '凭证号', '业务类型',
                    '物料编码', '物料名称', '数量',
                    '组织编码', '往来单位', '往来库区', '往来库位',
                    '仓库编码', '区位', '储位',
                    '记账日期', '创建日期', '记账人',
                    '凭证行', '指示号', '指示行'
                ]
                const filterVal = [
                    'printNumber', 'outOrderNumber', 'businessTypeName',
                    'materielCode', 'materielName', 'outStockNum',
                    'orgCode', 'lineCode', 'toWareAreaCode', 'toWareLocationCode',
                    'warehouseCode', 'wareAreaCode',
                    'wareLocationCode', 'accountDate', 'createTime',
                    'creator', 'rowNo', 'sourceOrderCode', 'sourceRowNo',
                ]
                const data = this.formatJson(filterVal, this.list3)
                console.log(data, '8')
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.locatorList, i, res)
                this.$set(this.list1[i], 'wareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.locateWarehouseCode, areaCode, i)
        },
        tochangeAreaSel (areaCode, i) {
            this.togetLocatorList(this.locateWarehouseCode, areaCode, i)
        },
        togetLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.tolocatorList, i, res)
                this.$set(this.list1[i], 'toWareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            })
        },
        update1 () {//客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQuery.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        update2 () { //客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQueryProofMain.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible1 = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleDblclickgysxz (row) {
            //父子页面通信
            this.parentsRows = row
            // this.gysxzDialogFormVisible = false;
        },
        handleClickgysxz () {
            this.khxzDialogFormVisible = true
        },
        handleClickgysxz1 () {
            this.khxzDialogFormVisible1 = true
        },
        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1]
            console.log(this.selectlistRow, "111")
            if (val.length > 1) {
                this.$refs.dd.clearSelection()
                this.$refs.dd.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.locateWarehouseCode = this.selectlistRow.warehouseCode
                this.getList1()
            }
        },
        selectRow2 (val) { //出库登记单击行选中方法
            this.selectlistRow2 = val
            console.log(val)
            this.locateWarehouseCode = val.warehouseCode
            this.$refs.dd.clearSelection()
            this.$refs.dd.toggleRowSelection(val)
        },

        selectRow3 (val) { //已选项选中行
            this.selectlistRow3 = val
            console.log(this.selectlistRow3, '9')
        },
        selectRowout (val) {//出库凭证复选框选中方法
            this.selectlistRowout = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRowout)

            if (this.selectlistRowout) {
                let stateList = this.dt_business_type_screenGroupNum_db.options.filter(v => {
                    return v.wareRuleCode == this.selectlistRowout.businessType
                })
                this.getList3(stateList)
            }
        },
        selectRowout1 (val) {//出库凭证单击行选中方法
            this.selectlistRowout1 = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        selectRowchange (val) {
            // this.selectlistRowchange = val;
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_business_type_screenGroupNum_db.options[0].wareRuleCode
                this.getList()
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_business_type_screenGroupNum_db.options[0].wareRuleCode
                this.getList2()
            }
        }
    }
};
