import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/outSourcing/outSourcingInstruct"
import { DT_ORG_TYPE } from "@/utils/constant"
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import * as api1 from "@/api/project/wms/productionMaterial/pullerStorage"
import { mapState } from "vuex"
import qrcode from "@/components/QRCode"
import gysxz from "@/components/Select/gysxz" //供应商选择
export default {
    name: "outSourcingInstruct",
    components: { Pagination, AuthoType, qrcode, gysxz },
    data () {
        return {
            list: null,
            subList: null,
            proofMainList: null,
            proofSubList: null,
            areaList: null,
            locatorList: [],
            toareaList: null,
            tolocatorList: [],
            value2: "",
            value3: "",
            valuedate: "",
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            theight: 0,
            lheight: 0,
            rheight: 0,
            Edit: false,
            state: true,
            activeName2: "first",
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                queryStateParam: undefined,
                orgCode: undefined,
                toOrgCode: undefined,
                toOrgName: undefined,
                warehouseCode: undefined,
                accountDate: this.timeFormat(),
                state: 'sales20'
            },
            queryState: ['ADD', 'PART'],
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuerySub: {
                currentPage: 1,
                pageSize: 10
            },
            dta: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                queryStateParam: undefined,
                instructCode: undefined
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                outOrderPlans: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            isChangeAddS: false,
            locateWarehouseCode: null,
            downloadLoading: false,
            dialogPrintVisible: false,
            gysxzDialogFormVisible: false,
            isEdit: true,
            DT_ORG_TYPE: DT_ORG_TYPE,
            printData: {},//打印数据
            tableKey: 0,
            parentsRows: []
        }
    },

    created () {
        //创建时加载备选项
    },
    computed: {
        ...mapState({
            dt_business_type_screenGroupNum_ww: state =>
                state.businessComponent.dt_business_type_screenGroupNum_ww
        })
    },
    mounted () {
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_business_type_screenGroupNum_ww"
        ])
        this.setTableHeight()
    },
    methods: {
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getProofMainList()
            } else {
                this.getList()
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 110
            this.lheight = this.$myFun.getSingleTbHeight() - 40
            this.rheight = this.$myFun.getSingleTbHeight() - 40
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_business_type_screenGroupNum_ww.options[0].wareRuleCode
            }
        },
        handleQuery () {
            if (this.listQuery.orgCode && this.listQuery.warehouseCode && this.listQuery.businessType) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.selected"),
                    type: "warning"
                })
            }

        },
        handleClickgysxz () {
            this.$refs.gysxz.dialogVisible = true
        },
        handleDblclickgysxz (row) { //父子页面通信
            this.parentsRows = row
            if (!this.parentsRows[0]) {
                this.listQuery.toOrgCode = ""
                this.listQuery.toOrgName = ""
                this.$refs.gysxz.dialogVisible = false
            } else {
                this.listQuery.toOrgCode = this.parentsRows[0].supplierCode
                this.listQuery.toOrgName = this.parentsRows[0].supplierName
                this.$refs.gysxz.dialogVisible = false
            }
        },
        handleQueryProof () {
            this.listQueryProofMain.currentPage = 1
            this.getProofMainList()
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日      
            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        getList () {//查询出库登记主表
            this.listQuery.queryStateParam = this.queryState.join(",")
            if (this.value2) {
                this.listQuery.deliverTimeStart = this.value2
            } else {
                this.listQuery.deliverTimeStart = ''
            }
            this.subList = []
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })

        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getSubList () {  //查询出库登记从表
            this.listQueryItems.queryStateParam = this.queryState.join(",")
            this.listQueryItems.instructCode = this.selectlistRow.instructCode
            api.queryRecorditems(this.listQueryItems).then(res => {
                res.data.list.forEach((item, index) => {
                    res.data.list[index].curQuantity = 0
                    res.data.list[index].invalidQuantity = res.data.list[index].instructNumber - res.data.list[index].outStockNum
                })
                this.subList = res.data.list
                for (let i = 0; i < this.subList.length; i++) {
                    this.locatorList.push([])
                }
                api1.getArea(this.locateWarehouseCode).then(resa => {
                    this.areaList = resa.data
                })
                api1.getArea(this.listQuery.towarehouseCode).then(resa => {
                    this.toareaList = resa.data
                })
            })
        },
        getProofMainList () {//查询出库凭证主表
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.proofSubList = []
            this.listQueryProofMain.sourceOrderCode = this.listQueryProofMain.instructCode
            api.queryRecordscb(this.listQueryProofMain).then(res => {
                this.proofMainList = res.data.list
                // this.total2=res.pages.count
            })
        },

        getProofSubList () {//查询出库凭证从表
            this.listQueryProofSub.outOrderNumber = this.selectlistRowProof.outOrderNumber
            this.listQueryProofSub.businessType = this.selectlistRowProof.businessType
            api.queryRecordsmx(this.listQueryProofSub).then(res => {
                this.proofSubList = res.data.list
                this.total3 = res.data.pages.count
            })
        },
        //保存
        handleSave () {
            if (this.listQuery.businessType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow.instructCode) {
                    let result = true
                    let saveList = []
                    let b = this.subList.map(v => {
                        v.materielCode = v.productCode
                        v.outStockNum = v.curQuantity
                        v.sourceDetailNo = v.rowNo
                        return v
                    })
                    b.forEach(item => {
                        if (item.outStockNum > 0) {
                            result = false
                            saveList.push(item)
                        }
                    })
                    if (result) {
                        this.$message({
                            title: "警告",
                            message: "本次收货量不可全部为0或空",
                            type: "warning"
                        })
                    } else {
                        this.Edit = true
                        this.dto.outOrderPlans = saveList
                        this.dto.businessType = this.listQuery.businessType
                        this.dto.accountDate = this.listQuery.accountDate
                        this.dto.orgCode = this.selectlistRow.orgCode
                        this.dto.customerCode = this.selectlistRow.customerCode
                        this.dto.sourceOrderCode = this.selectlistRow.instructCode
                        this.dto.warehouseCode = this.selectlistRow.warehouseCode
                        api.Save(this.dto).then(() => {
                            this.$notify({
                                title: "成功",
                                message: "保存成功",
                                type: "success",
                                duration: 2000
                            })
                            this.getSubList()
                            this.state = false
                            this.Edit = false
                        })
                        if (this.state) {
                            this.Edit = false
                        }
                    }
                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                })
            }
        },

        //复制
        handleCopy () {
            if (this.selectlistRowSub.length) {
                this.subList.map((item, index) => {
                    this.selectlistRowSub.forEach(items => {
                        if (items.productCode === item.productCode) {
                            this.subList[index].curQuantity = item.invalidQuantity
                            this.$set(this.subList, index, this.subList[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '打印次数', '凭证号', '业务类型',
                    '物料编码', '物料名称', '已出库',
                    '组织编码', '委外单位', '委外库区',
                    '委外库位', '区位', '储位',
                    '仓库编码', '记账日期', '创建时间',
                    '记账人', '凭证行号', '出库指示',
                    '行号'
                ]
                const filterVal = [
                    'printNumber', 'outOrderNumber', 'businessType',
                    'materielCode', 'materielName', 'outStockNum',
                    'orgCode', 'warehouseCode', 'toWareAreaCode',
                    'toWareLocationCode', 'WareAreaCode',
                    'WareLocationCode', 'warehouseCode', 'accountDate',
                    'createTime', 'creator', 'rowId',
                    'sourceOrderCode', 'rowNo'
                ]
                const data = this.formatJson(filterVal, this.list)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowProof) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowProof
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })

            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.locatorList, i, res.data)
                this.$set(this.subList[i], 'wareLocationCode', res.data[0].locationCode)
                this.$set(this.subList, i, this.subList[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.locateWarehouseCode, areaCode, i)
        },
        togetLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.tolocatorList, i, res.data)
                this.$set(this.subList[i], 'toWareLocationCode', res.data[0].locationCode)
                this.$set(this.subList, i, this.subList[i])
            })
        },
        tochangeAreaSel (areaCode, i) {
            this.togetLocatorList(this.locateWarehouseCode, areaCode, i)
        },
        update2 () {//客户选中弹窗确认按钮
            if (this.parentsRows) {
                this.listQueryProofMain.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible1 = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.dd.clearSelection()
                this.$refs.dd.toggleRowSelection(val.pop())
            }
            this.$refs.dd.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.locateWarehouseCode = this.selectlistRow.warehouseCode
                this.getSubList()
            }
        },
        selectRowMain (val) {//出库登记单击行选中方法
            this.selectlistRowMain = val
            this.locateWarehouseCode = val.warehouseCode
            this.$refs.dd.clearSelection()
            this.$refs.dd.toggleRowSelection(val) //单击行绑定点击事件
        },

        selectRowSub (val) {//已选项选中行
            // this.$emit('sendVal' ,val)
            this.selectlistRowSub = val
        },
        selectRowProof (val) {//出库凭证复选框选中方法
            this.selectlistRowProof = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRowProof)
            if (this.selectlistRowProof) {
                this.getProofSubList()
            }
        },
        selectRowProofMain (val) {//出库凭证单击行选中方法
            this.selectlistRowPmain = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val) //单击行绑定点击事件
        },
        selectRowProofSub (val) {
            this.selectlistRowchange = val
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_business_type_screenGroupNum_ww.options[0].wareRuleCode

            }
        }
    }
};
