import * as api from "@/api/project/srm/priceManage/reductionLetter"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
export default {
    name: "wlxz",
    components: { Pagination, caiGou, dialogGysxz },
    props: ['unMutiple', 'title', 'isRequired', 'orgCode', 'supplierCode', 'supplierName'],
    data () {
        return {
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                materielCode: undefined,//物料编码
                materielName: undefined,//物料名称
                materielType: undefined,//类型
                materielClassify: undefined,//分类
                materielGroup: undefined//物料组
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            dialogVisible: false
        }
    },
    computed: {
        ...mapState({

        })
    },
    mounted () {

    },
    watch: {
        orgCode: {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.getList()
            },
            deep: true
        },
        supplierCode: {
            handler (newValue) {
                this.listQuery.supplierCode = newValue
                this.getList()
            },
            deep: true
        },
        supplierName: {
            handler (newValue) {
                this.listQuery.supplierName = newValue
                this.getList()
            },
            deep: true
        },
    },
    methods: {
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            api.queryRecordPurchase(this.listQuery).then(res => {
                res = res.data
                this.list = res.list.map(v => {
                    v.calculateDate = this.datedifference(v.validTimeStart, v.validTimeEnd)
                    return v
                })
                this.list = this.arrayUnique2(this.list, 'materielCode', 'calculateDate')
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.map((item, index) => {
                            if (item.materielCode === items.materielCode) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                            return item
                        })
                    })
                }
            })
        },
        //去最小值
        arrayUnique2 (arr, name, comp) {
            let newList = []
            for (let i = 0; i < arr.length; i++) {
                let hash = arr[i]
                for (let j = i + 1; j < arr.length; j++) {
                    if (arr[j][name] == hash[name]) {
                        hash = arr[i][comp] > arr[j][comp] ? arr[i] : arr[j]
                    }
                }
                newList.push(hash)
            }
            return this.setData(newList, name)
        },
        //去重
        setData (arr, name) {
            var hash = {}
            return arr.reduce(function (item, next) {
                hash[next[name]] ? '' : hash[next[name]] = true && item.push(next)
                return item
            }, [])
        },
        //计算日期的差值
        datedifference (sDate1, sDate2) {    //sDate1和sDate2是2006-12-18格式  
            let dateSpan
            let iDays
            sDate1 = Date.parse(sDate1)
            sDate2 = Date.parse(sDate2)
            dateSpan = sDate2 - sDate1
            dateSpan = Math.abs(dateSpan)
            iDays = Math.floor(dateSpan / (24 * 3600 * 1000))
            return iDays
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        } else {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        }
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                }
                this.dialogVisible = false
            }
        },
    }
};
