import * as api from "@/api/project/srm/priceManage/priceConfirmList"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
export default {
    name: "wlxz",
    components: { Pagination, caiGou, dialogGysxz },
    props: ['unMutiple', 'title', 'isRequired', 'orgCode', 'supplierName', 'supplierCode'],
    data () {
        return {
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                state: 'P002',
                materielCode: undefined,//物料编码
                materielName: undefined,//物料名称
                orgCode: undefined,
                supplierName: undefined,
                supplierCode: undefined
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            dialogVisible: false
        }
    },
    computed: {
        ...mapState({
            dt_srm_pc6: state => state.dict.dt_srm_pc6,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pc6"])
    },
    watch: {
        orgCode: {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.getList()
            },
            deep: true
        },
        supplierName: {
            handler (newValue) {
                this.listQuery.supplierName = newValue
                this.getList()
            },
            deep: true
        },
        supplierCode: {
            handler (newValue) {
                this.listQuery.supplierCode = newValue
                this.getList()
            },
            deep: true
        },
    },
    methods: {
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            api.queryRecordItems(this.listQuery).then(res => {
                let options = [this.dt_srm_pc6]
                res.data.list = this.$myFun.codeToName(res.data.list, options, ['dataSource'])
                this.list = res.data.list
                this.total = res.data.list.length
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.materielCode === items.materielCode) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        } else {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        }
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                }
                this.dialogVisible = false
            }
        },
    }
};
