import * as api from "@/api/project/srm/newProducts/projectMasterData"
import { queryRecords } from "@/api/project/srm/dataBase/dictMaintenance"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import addMain from "./dialog/addMain"
import close from "./dialog/close"
export default {
    name: "projectMasterData",
    components: { Pagination, caiGou, addMain, close },
    data () {
        return {
            typeList: [],
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            dialogStatus: '',
            dialogVisible: false,
            listQuery: {
                //查询
                orgCode: 'dtcg0001',
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                closeExplain: undefined
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getTypeData()
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        getTypeData () {
            let dto = {
                currentPage: 1,
                pageSize: 15,
                parentCode: 'dt_srm_pb7'
            }
            queryRecords(dto).then(res => {
                let options = [this.dt_srm_p0]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state'])
                this.typeList = res.data.list
            })
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            let dto = {
                currentPage: 1,
                pageSize: 15,
                parentCode: 'dt_srm_pb7'
            }
            queryRecords(dto).then(resa => {
                api.queryRecords(this.listQuery).then(res => {
                    let options = [this.dt_srm_p0]
                    res.data = this.$myFun.codeToName(res.data, options, ['state'])
                    resa.data.list.forEach(v => {
                        res.data.forEach(val => {
                            if (v.code == val.projectType) {
                                val.projectTypeCN = v.name
                            }
                        })
                    })
                    this.list = res.data
                    this.total = res.data.length
                })
            })

        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
            this.$nextTick(() => {
                this.$refs.addMain.initForm()
            })

        },
        handleUpdate () {
            if (this.selectlistRow) {
                this.$refs.addMain.dialogStatus = 'update'
                this.$refs.addMain.dialogVisible = true
                this.$refs.addMain.temp = { ...this.selectlistRow }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectlistRow) {
                this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let dto = {
                            rowIds: [this.selectlistRow.rowId]
                        }
                        api.deleteRecord(dto).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }

        },
        handleSubmit () {
            //提交
            if (this.selectlistRow) {
                this.$confirm(`是否提交？`, "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        method: 'commit',
                        rowId: this.selectlistRow.rowId
                    }
                    api.submitRecords(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '提交成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleClose () {
            //关闭
            if (this.selectlistRow) {
                this.$refs.close.dialogVisible = true
                this.$refs.close.temp.rowId = this.selectlistRow.rowId
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
