import * as api from "@/api/project/srm/supplierManage/supplierAccess/investigatePlan"
export default {
    name: 'tabProject',
    data () {
        return {
            tableData: [],
            oprateBtnConfig: [
                { id: 0, class: 'filter-item', icon: 'el-icon-plus', clickMethod: 'handleAdd', name: '新增' },
                { id: 1, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleRefresh', name: '刷新' },
            ],
            tableConfig: [
                // { label: '序号', prop: 'prop1', align: 'center', width: 'auto' },
                { label: '考察计划号', prop: 'investigateNo', align: 'center', width: 'auto' },
                { label: '提案号', prop: 'proposalNo', align: 'center', width: 'auto' },//无
                { label: '物料分类', prop: 'materielClass', align: 'center', width: 'auto' },
                { label: '物料分类名称', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '平台系列', prop: 'platformSeriesCN', align: 'center', width: 'auto' },
                { label: '平台定位', prop: 'positionCN', align: 'center', width: 'auto' }
            ]
        }
    },
    methods: {
        handleAdd () {
            this.$emit('checkAdd', 'class')
        },
        oprateAdd () {
            let ldata = JSON.parse(JSON.stringify(this.tableData))
            this.$emit('showProposal', ldata)
        },
        handleDelete (index, row) {
            if (row.rowId) {
                api.deleteClasses(row.investigateNo, row.rowId).then(() => {
                    this.$message({ type: 'success', message: '删除成功' })
                    this.tableData = this.tableData.filter(item => {
                        return item.rowId !== row.rowId
                    })
                })
            } else {
                this.tableData = this.tableData.filter((item, indexf) => {
                    console.log(item)
                    return indexf !== index
                })
            }
        },
        handleRefresh () {

        },
        initTable (data) {
            this.tableData = data
        },
        proposalConfirm (data) {
            console.log(data)
            this.tableData = data
        }
    }
}
