export default {
    name: 'bar',
    props: {
        chartsData: {
            type: Object,
            default: undefined
        },
        name: {
            type: String,
            default: ''
        },
        id: {
            type: String,
            default: ''
        },
        xAxis: {
            type: Array,
            default: undefined
        },
        yAxis: {
            type: Array,
            default: undefined
        },
        legend: {
            type: Array,
            default: undefined
        },
        xRotate: {
            type: Number,
            default: 0
        },
        gridLeft: {
            type: Number,
            default: 8
        },
        height: {
            type: Number,
            default: 300
        }
    },
    data () {
        return {}
    },
    watch: {
        chartsData: {
            handler () {
                this.down()
            },
            deep: true,
            immediate: true
        }
    },
    methods: {
        down () {
            let axisLine = {
                lineStyle: {
                    color: '#d9d9d9'
                }
            }
            let opt = {
                title: { text: this.name },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'shadow'
                    }
                },
                grid: {
                    left: `${this.gridLeft}%`
                },
                xAxis: {
                    data: this.xAxis,
                    axisLabel: {
                        //坐标轴刻度标签的相关设置。
                        interval: 0,
                        rotate: this.xRotate
                    },
                    axisLine: axisLine
                },
                yAxis: {
                    data: this.yAxis,
                    axisLine: { show: false, lineStyle: axisLine.lineStyle },
                    axisTick: { show: false }
                },
                legend: {
                    data: this.legend
                },
                series: this.chartsData.data
            }
            this.$nextTick(() => {
                var myChart = this.$echarts.init(this.$refs[this.id])
                myChart.setOption(opt, true)
            })
        }
    }
}
