import * as api from '@/api/project/mes/dataBase/processProcess/processProcessSelect'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'processProcessSelect',
    components: { Pagination },
    props: ['orgCode', 'unMutiple', 'stockType', 'warehouseCode'],
    data() {
        return {
            list: null,
            total: 0,
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0
        }
    },
    filters: {
        isEnableFilter: function(e) {
            if (e === 'step_1') {
                return '是'
            } else {
                return '否'
            }
        }
    },
    computed: {
        ...mapState({
            dt_stock_type: state => state.dict.dt_stock_type,
            dt_stock_status: state => state.dict.dt_stock_status,
            dt_materiel_type: state => state.dict.dt_materiel_type
        })
    },
    mounted() {
        this.$store.dispatch('dict/getDicData', [
            'dt_stock_type',
            'dt_stock_status',
            'dt_materiel_type',
            'dt_stock_type'
        ])
        this.getEquipmentData()
    },
    methods: {
        // 父级工位表格展示已选，modal已选勾选
        getListSelected() {
            this.listSelected = this.$parent.$parent.$parent.list_b
        },
        checked(i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
        },
        //检查是否取消勾选
        checkUnsel(val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.stepCode === row.stepCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain(val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.stepCode === items.stepCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.stepCode !== row.stepCode
                })
            }
        },
        selectAll(val) {
            if (val.length == 0) {
                this.list.forEach(item => {
                    this.listSelected.forEach((item2, index) => {
                        if (item.stepCode == item2.stepCode) {
                            this.listSelected.splice(index, 1)
                        }
                    })
                })
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.stepCode === items.stepCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 去除已选
        getEquipmentData() {
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.stepCode === items.stepCode) {
                                this.$nextTick(function() {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        }
    }
}
