import * as api from "@/api/project/srm/supplierSelection/constantParameters"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
export default {
    components: { Pagination, caiGou },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,
            total: 0,
            mainFormList: {
                rowId: undefined
            },
            list: [],
            edlist: [],
            status: null,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            rules: {
                orgCode: [{ required: true, message: '组织编码不能为空', trigger: 'blur' }],
                constantType: [{ required: true, message: '常量类型不能为空', trigger: 'blur' }],
                constantValue: [{ required: true, message: '常量值不能为空', trigger: 'blur' }],
            }

        }
    },
    computed: {
        ...mapState({
            dt_srm_pde: state => state.dict.dt_srm_pde,
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('dict/getDicData', ['dt_srm_pde'])
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() + 8
        },
        //获取组织编码名称
        selectName (val) {
            this.mainFormList.purchaseOrgName = val
        },
        getName () {
            this.edlist = this.dt_srm_pde.filter(item => {
                return item.dictItemKey === this.mainFormList.typeEncoding
            })
            this.mainFormList.typeName = this.edlist[0].dictItemValue
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //保存按钮单击事件
        handleSave () {
            this.$refs.mainForm.validate(valid => {
                if (valid) {
                    if (this.status === 'update') {
                        api.updateRecord(this.mainFormList.rowId, this.mainFormList).then(() => {
                            this.$message({
                                title: '成功',
                                message: '编辑成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.status = 'create'
                            this.resetTemp()
                            this.getList()
                        })
                    } else {
                        api.addRecord(this.mainFormList).then(() => {
                            this.$message({
                                title: '成功',
                                message: '保存成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.resetTemp()
                            this.getList()
                        })
                    }

                } else {
                    return false
                }
            })
        },
        handleUpdate (index, val) {
            console.log(index, val)
            this.mainFormList = { ...val }
            this.status = 'update'

        },
        handleDelete (index, val) {
            console.log(index, val)
            api.deleteRecord(val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.resetTemp()
                this.getList()
            })
        },
        resetTemp () {
            this.mainFormList = {
                purchaseOrg: undefined,
                typeEncoding: undefined,
                constantValue: undefined,
                remark: undefined
            }
        }
    }
}
