import * as api from '@/api/data-base/processRoute'
import Pagination from '@/components/Pagination'
import orgSelect from "@/components/Select/orgSelect.vue"
import { DT_ORG_TYPE } from "@/utils/constant"
import { mapState } from 'vuex'
export default {
    name: 'processRoute',
    components: { Pagination, orgSelect },
    data () {
        return {
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            fromList: [],
            total: 0,
            total1: 0,
            listQuery: {
                //查询
                productProcessCode: undefined,
                currentPage: 1,
                pageSize: 15
            },
            listQuery1: {
                //查询
                currentPage: 1,
                pageSize: 15

            },
            temp: {
                productProcessCode: undefined,
                endTime: undefined,
                version: undefined,
                orgCode: undefined,
                orgName: undefined,
                productCode: undefined,
                startTime: undefined,


            },
            tempFrom: {
                eoq: undefined,
                isEnd: 1,
                isFirst: 1,
                note: undefined,
                processCode: undefined,
                readyTime: undefined,
                stationCode: undefined,
                supplierCode: undefined,
                transferTime: undefined,
                workTime: undefined

            },
            dialogFormVisible: false,
            fromDialogFormVisible: false,
            dialogStatus: '',
            fromDialogStatus: '',
            DT_ORG_TYPE: DT_ORG_TYPE,
            currentSelectedRow: null, //当前选中的行
            selectedRows: null, //多选时选中的所有行
            tableKey: [0, 1]//表格索引
        }
    },
    created () { },
    filters: {
        isFilter: function (e) {
            if (e === '1') {
                return '是'
            } else {
                return '否'
            }
        }
    },
    computed: {
        ...mapState({
            dt_role_type: state => state.dict.dt_role_type
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_role_type'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //获取主表表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //获取从表数据
        getFromList () {
            api.queryDetailRecords(this.selectedRows.productProcessCode).then(res => {
                this.fromList = res.data
                // this.total1 = res.data.pages.count
            })

        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleCloseFrom () {
            this.resetFromTemp()
            this.$refs.tempFrom.resetFields()

        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        orgname (name) {
            this.temp.orgName = name;
        },
        resetTemp () {
            this.temp = {
                productProcessCode: undefined,
                endTime: undefined,
                version: undefined,
                orgCode: undefined,
                orgName: undefined,
                productCode: undefined,
                startTime: undefined,
            }
        },
        resetFromTemp () {
            this.tempFrom = {
                eoq: undefined,
                isEnd: '1',
                isFirst: '1',
                note: undefined,
                processCode: undefined,
                readyTime: undefined,
                stationCode: undefined,
                supplierCode: undefined,
                transferTime: undefined,
                workTime: undefined

            }

        },
        handleCreate () {
            //新增按钮单击事件方法
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create () {
            api.addRecord(this.temp).then(() => {
                this.getList()
                this.dialogFormVisible = false
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
            })
        },
        handleUpdate () {
            //编辑按钮单击事件方法
            if (this.selectedRows) {
                this.temp = this.selectedRows
                api.getRecord(this.selectedRows.productProcessCode).then(res => {
                    this.temp = res.data;
                    this.dialogStatus = "update";
                    this.dialogFormVisible = true;
                });
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update () {
            //编辑确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.updateRecord(this.temp.productProcessCode, this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '更新成功',
                            type: 'success',
                            duration: 2000
                        })
                    }
                    )
                }
            })
        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectedRows) {
                this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        api.deleteRecord(this.selectedRows.productProcessCode).then(
                            () => {
                                this.getList(),
                                    this.$message({
                                        title: '成功',
                                        message: '删除成功',
                                        type: 'success',
                                        duration: 2000
                                    })
                            }
                        )
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handlePublish () {
            //发布
            if (this.selectedRows) {
                api.publishRecord(this.selectedRows.productProcessCode).then(() => {
                    this.getList(),
                        this.$message({
                            title: '成功',
                            message: '发布成功',
                            type: 'success',
                            duration: 2000
                        })
                });
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }

        },
        handleCreateFrom () {
            this.resetFromTemp()
            if (this.selectedRows) {
                this.fromDialogStatus = 'create'
                this.fromDialogFormVisible = true

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品工艺编码',
                    type: 'warning'
                })
            }
        },
        handleUpdateFrom () {
            if (this.selectedRows) {
                if (this.selectFromList) {
                    api.getDetailRecord(this.selectedRows.productProcessCode, this.selectFromList[0].processCode).then(res => {
                        this.tempFrom = res.data;
                        this.fromDialogStatus = "update";
                        this.fromDialogFormVisible = true;
                    });
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择工序编码',
                        type: 'warning'
                    })

                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品工艺编码',
                    type: 'warning'
                })
            }

        },
        handleDeleteFrom () {
            if (this.selectedRows) {
                if (this.selectFromList) {
                    this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    })
                        .then(() => {
                            api.deleteDetailRecord(this.selectedRows.productProcessCode, this.selectFromList[0].processCode).then(
                                () => {
                                    this.getFromList(),
                                        this.$message({
                                            title: '成功',
                                            message: '删除成功',
                                            type: 'success',
                                            duration: 2000
                                        })
                                }
                            )
                        })
                        .catch(() => {
                            this.$message({
                                type: 'info',
                                message: '已取消删除'
                            })
                        })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择产品工艺编码',
                        type: 'warning'
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品工艺编码',
                    type: 'warning'
                })
            }

        },
        createFrom () {
            api.addDetailRecord(this.selectedRows.productProcessCode, this.tempFrom).then(() => {
                this.getFromList()
                this.fromDialogFormVisible = false
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
            })

        },
        updateFrom () {
            api.updateDetailRecord(this.selectedRows.productProcessCode, this.selectFromList[0].processCode, this.tempFrom).then(() => {
                this.getFromList()
                this.fromDialogFormVisible = false
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
            })

        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectedRows = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.tb.clearSelection();
                this.$refs.tb.toggleRowSelection(val[val.length - 1]);
            }
            this.$refs.tb.setCurrentRow(this.selectedRows);
            if (this.selectedRows) {
                this.getFromList();
            }
        },
        //从表选择框选中
        selectFromChange (val) {
            this.selectFromList = val

        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
