import * as api from '@/api/project/ems/dataBase/energyBalance.js'
import Pagination from '@/components/Pagination'
import throttle from 'lodash/throttle'
export default {
    name: 'energyBalance',
    components: { Pagination },
    data () {
        return {
            modalnum: null, //模板编号，非模板页面可删
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                balanceCode: undefined,
                balanceName: undefined,
                remark: undefined,
                statusName: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            currentSelectedRow: null, //当前选中的行
            selectedRows: null, //多选时选中的所有行
            selectlistRow: null,
            tableKey: 0, //表格索引
            rules: {
                balanceCode: [
                    {
                        required: true,
                        message: '请选输入能源平衡表编码',
                        trigger: 'blur'
                    },
                    {
                        max: 50,
                        message: '长度不超过50',
                        trigger: 'blur'
                    }
                ],
                balanceName: [
                    {
                        required: true,
                        message: '请选输入能源平衡表名称',
                        trigger: 'blur'
                    },
                    {
                        max: 50,
                        message: '长度不超过50',
                        trigger: 'blur'
                    }
                ],
                remark: [
                    {
                        max: 50,
                        message: '长度不超过50',
                        trigger: 'blur'
                    }
                ]
            }
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    destroyed () {
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.query(this.listQuery).then(response => {
                if (response.data) {
                    response.data.list.map((v, i) => {
                        v.serial = i + 1
                    })
                    this.list = response.data.list
                    this.total = response.data.pages.count
                }

            })
        },
        resetTemp () {
            this.temp.balanceCode = undefined
            this.temp.balanceName = undefined
            this.temp.remark = undefined
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        //新增确认
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        api.create(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑按钮单击事件方法
        handleUpdate () {
            if (this.currentSelectedRow && this.selectedRows.length == 1) {
                this.temp = JSON.parse(JSON.stringify(this.currentSelectedRow))
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //编辑确认
        update: throttle(
            function () {
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        api.update(this.temp.balanceCode, this.temp).then(
                            () => {
                                this.getList()
                                this.dialogFormVisible = false
                                this.$message({
                                    title: '成功',
                                    message: '更新成功',
                                    type: 'success',
                                    duration: 2000
                                })
                            }
                        )
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除按钮单击事件方法
        handleDelete () {
            if (this.selectedRows && this.selectedRows.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectedRows.forEach((val, index) => {
                            ids[index] = val.balanceCode
                        })
                        api.deleteItems(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectedRows = val
            this.currentSelectedRow = val[val.length - 1]
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
