import * as api from "@/api/project/srm/contractManage/templateMaintenance"
export default {
    props: ['rowId'],
    components: {},
    data () {
        return {
            tableKey: 0,
            list: [],
            dialogStatus: '',
            dialogVisible: false

        }
    },
    methods: {
        getList () {
            let dto = {
                rowId: this.rowId,
                itemType: 'nomal'
            }
            api.queryRecordsItem(dto).then(res => {
                this.list = res.data.list
            })
        },
        getListData (val) {
            let dto = {
                rowId: val.rowId,
                itemType: 'nomal'
            }
            api.queryRecordsItem(dto).then(res => {
                this.list = res.data.list
            })
        },
        handleDelete (index, val) {
            api.deleteRecordItem(val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getList()
            })
        },
        handleCreate () {
            this.$emit('create', 'contentB')
        },
        handleUpdate (index, val) {
            this.$emit('update', 'contentB', val)
        },
    }
}
