import * as api from '@/api/project/wms/materialScrap/scrapApplication'
import * as scrap from '@/api/project/wms/materialScrap/scrap'
import { mapState } from 'vuex';
import Pagination from '@/components/Pagination' // 分页组件
import warehouseSelect from '@/components/Select/warehouse-select.vue'//下拉组件
import AuthoType from '@/components/Select/AuthoType.vue'//权限类型下拉组件
export default {
    name: "scrap",
    components: { Pagination, warehouseSelect, AuthoType },
    data () {
        return {
            total_a: 0,
            total_a_1: 0,
            total_b_1: 0,
            list_a: [],
            list_a_1: [],
            list_b: [],
            list_b_1: [],
            theight: 0,
            lheight: 0,
            rheight: 0,
            activeName2: 'first',
            listQuery_a: {
                // state: 'scrap20',
                currentPage: 1,
                pageSize: 10,
                businessType: '0550',//报废业务类型
            },
            listQuery_a_1: {},
            listQuery_b: {
                currentPage: 1,
                businessType: '0550',//报废业务类型
                pageSize: 1000,
            },
            listQuery_b_1: {
                currentPage: 1,
                pageSize: 10,
                businessType: '0550',//报废业务类型
            },
            saveDate: this.timeFormat(),//记账日期
        }
    },
    computed: {
        ...mapState({
            dt_org_code_factory: state => state.businessComponent.dt_org_code_factory,
            dt_materiel_transfer_type_bf: state => state.businessComponent.dt_materiel_transfer_type_bf,
        }),
        dt_type () {
            return this.dt_materiel_transfer_type_bf.options
        }
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('businessComponent/getBusinessComponentData', ['dt_org_code_factory', 'dt_materiel_transfer_type_bf']);
    },
    methods: {
        handleTabClick (tab) {//选项卡的切换
            if (tab.name == "first") {
                // this.getList_a()
            } else {
                this.getList_b()
            }
        },
        timeFormat () {
            var d = new Date();
            var year = d.getFullYear();       //年  
            var month = d.getMonth() + 1;     //月  
            var day = d.getDate();            //日  

            var clock = year + "-";
            if (month < 10)
                clock += "0";
            clock += month + "-";

            if (day < 10)
                clock += "0";
            clock += day + " ";
            return (clock);
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 110;
            this.lheight = this.$myFun.getSingleTbHeight() + 85;
            this.rheight = this.$myFun.getSingleTbHeight() - 60;
        },
        getList_a () {
            api.getData_a(this.listQuery_a).then(res => {
                this.list_a = res.data.list
                this.total_a = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            });
        },
        checked () {
            this.$refs.tb_a.toggleRowSelection(this.list_a[0], true);//官网方法，重点
        },
        isIntegerNotMust (event) {//验证正整数
            var value = event.target.value;
            if (!/^\+?[1-9][0-9]*$/.test(value)) {
                this.$message({
                    title: "警告",
                    message: "只能正整数",
                    type: "warning"
                });
                event.target.value = '';
            }
        },
        getList_a_1 () {//报废明细
            this.listQuery_b.scrapOrderCode = this.selectListRow_a.scrapOrderCode
            api.getData_a_1(this.listQuery_b).then(res => {
                this.list_a_1 = res.data.list.map((v) => {
                    v.outStockNum1 = v.outStockNum
                    return v
                })
                this.total_a_1 = res.data.pages.count
            })
        },
        getList_b () {//报废凭证
            scrap.getData_a(this.listQuery_b).then(res => {
                this.list_b = res.data.list

            })
        },
        getList_b_1 () { },
        selectRow_a (val) {
            if (val.length > 1) {
                this.$refs.tb_a.clearSelection()//清除其他行的选中
                this.$refs.tb_a.toggleRowSelection(val[val.length - 1], 'selected')//单击行绑定点击事件
            } else if (val.length === 1) {
                this.selectListRow_a = val[val.length - 1]
                this.getList_a_1()
            }
        },
        selectRow_a_1 (val) {
            this.selectListRow_a_1 = val
        },
        selectRow_b () { },
        selectRow_b_1 () { },
        clickRow_a (val) {
            this.$refs.tb_a.clearSelection()//清除其他行的选中
            this.$refs.tb_a.toggleRowSelection(val)//单击行绑定点击事件
        },
        clickRow_a_1 () { },
        clickRow_b (val) {
            this.selectListRow_b = val
            this.listQuery_b_1.outOrderNumber = this.selectListRow_b.outOrderNumber
            scrap.getData_a_1(this.listQuery_b_1).then(res => {
                this.list_b_1 = res.data.list
                this.total_b_1 = res.data.pages.count

            })
        },
        handleSave () {//保存
            if (this.saveDate) {
                this.dto = this.selectListRow_a;
                this.dto.accountDate = this.saveDate;
                this.dto.sourceOrderCode = this.selectListRow_a.scrapOrderCode;
                this.dto.toOrgCode = this.selectListRow_a.orgCode;
                this.dto.outOrderPlans = this.list_a_1.map((v) => {
                    v.sourceDetailNo = v.rowNo;
                    v.outStockNum = v.StockNum || 1;
                    v.toWareAreaCode = "*";
                    v.toWareLocationCode = "*";
                    return v
                });
                scrap.creatData_a(this.dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '保存成功',
                        type: 'success',
                    })
                    this.getList_a_1()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择记账日期',
                    type: 'warning',
                })
            }
        },
        change () {
            this.list_a_1.map((item, index) => {
                this.$set(this.list_a_1, index, this.list_a_1[index]);
            });
        },
        handleCopy () {//数量复制
            if (this.selectListRow_a_1 && this.selectListRow_a_1.length > 0) {
                this.list_a_1.map((item, index) => {
                    this.selectListRow_a_1.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.list_a_1[index].StockNum = (item.scrapNumber - item.outStockNum);
                            this.$set(this.list_a_1, index, this.list_a_1[index]);
                        }
                    });
                });
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        },
        exportData () {//导出
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = ['打印次数', '凭证号', '业务类型', '物料编码', '物料名称', '数量',
                    '组织编码', '仓库编码', '区位', '储位', '库位', '记账日期', '申请单号',
                    '行号', '创建日期', '记账人']
                const filterVal = ['printNumber', 'outOrderNumber', 'businessTypeName', 'materielCode',
                    'materielName', 'outStockNum', 'orgCode', 'warehouseCode', 'wareAreaCode',
                    'wareLocationCode', 'wareLocationCode', 'accountDate', 'sourceOrderCode', 'rowNo',
                    'createTime', 'creator']
                const data = this.formatJson(filterVal, this.list_b_1)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        setdefault (val, i) {
            if (i === 1) {
                this.$set(this.listQuery_a, 'orgCode', val)
            } else if (i === 3) {
                this.$set(this.listQuery_a, 'warehouseCode', val)
                // this.getList_a();
            }
        },
        //打印页面内容
        warehousePrint () {
            let wpt = document.getElementById('table-content');
            let newContent = wpt.innerHTML;
            let oldContent = document.body.innerHTML;

            document.body.innerHTML = newContent;
            window.print(); //打印方法
            window.localtion.reload();
            document.body.innerHTML = oldContent;
        }
    },

}
