import * as api from '@/api/project/srm/supplierManage/supplierConfigure'
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    data () {
        return {
            temp: {},
            dialogStatus: '',
            dialogVisible: false,
            rowId: null,
            Edit: false
        }
    },
    computed: {
        ...mapState({
            dt_srm_par: state => state.dict.dt_srm_par,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_par"])
    },
    methods: {
        changeCode (val) {
            if (val === 'PAR1' || val === 'PAR0') {
                this.Edit = true
            } else {
                this.Edit = false
            }
        },
        create: throttle(function () {
            api.addRecord(this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        update: throttle(function () {
            api.updateRecord(this.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                type: undefined,
                represent: undefined,
                changeMode: undefined,
                processNumber: undefined,
                remark: undefined
            }
        }
    }
}
