import * as api from '@/api/project/srm/supplierManage/supplierConfigure'
import Pagination from '@/components/Pagination'
export default {
    components: { Pagination },
    data () {
        return {
            dialogVisible: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            isSingle: false,
            tableKey: 0,
            dataList: [],
            total: 0,
            rowId: null
        }
    },
    mounted () {
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecordAttribute(this.listQuery).then(res => {
                this.dataList = res.data.list
                this.total = res.data.pages.count
                this.dataList.forEach((item, index) => {
                    item.rowId = (this.listQuery.currentPage - 1) * this.listQuery.pageSize + index + 1
                })
            })
        },
        handleQuery () {
            this.getList()
        },
        confirm () {
            if (this.selectedRows && this.selectedRows.length > 0) {
                let b
                b = this.selectedRows.map(v => {
                    let a = {}
                    a.fieldCode = v.columnName
                    a.fieldName = v.columnComment
                    return a
                })
                console.log(b, 999999)
                // api.addRecordItem(this.rowId, b).then(() => {
                //     this.$message({
                //         title: '成功',
                //         message: '新增成功',
                //         type: 'success',
                //         duration: 2000
                //     })
                //     this.dialogVisible = false
                //     this.$emit('getFreshSubList')
                // })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
}
