import Pagination from '@/components/Pagination' // Secondary package based on el-pagination
import * as api from '@/api/project/mes/dataBase/stationProduct/stationProduct'

export default {
    name: 'stationEquipment',
    components: { Pagination },
    data() {
        return {
            theight: 0, //表格高度
            list_a: [],
            total_a: 0,
            listQuery_a: {
                currentPage: 1,
                pageSize: 10,
                stationCode: undefined,
                stationName: undefined
            }
        }
    },
    computed: {},
    mounted() {
        this.setTableHeight()
        this.getList_a()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight() + 20
        },
        // 查询工位列表
        getList_a() {
            this.list_b = []
            this.list_c = []
            api.getStationList(this.listQuery_a).then(res => {
                this.list_a = res.data.list
                this.total_a = res.data.pages.count
            })
        },
        selectRow_a(val) {
            this.select_a = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$emit('handSelectSelectRow', this.select_a)
        }
    }
}
