import { queryRecords } from "@/api/project/srm/dataBase/dictMaintenance"
import { mapState } from "vuex"
export default {
    data () {
        return {
            phaseList: [],
            dialogVisible: false,
            tableKey: 0,
            list: [],
            temp: {},
        }
    },
    computed: {
        ...mapState({
            dt_srm_pak: state => state.dict.dt_srm_pak,
            dt_srm_pb2: state => state.dict.dt_srm_pb2,
            dt_srm_pam: state => state.dict.dt_srm_pam,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pak", 'dt_srm_pb2', 'dt_srm_pam'])
        this.getList()
    },
    watch: {
        'temp.paymentCategory' (val) {
            if (!val) {
                this.temp.paymentPhase = undefined
                this.phaseList = []
            } else {

                this.changeData(val)
            }
        },
    },
    methods: {
        changeData (val) {
            if (val) {
                let dto = {
                    currentPage: 1,
                    pageSize: 15,
                    parentCode: val
                }
                queryRecords(dto).then(res => {
                    let options = [this.dt_srm_p0]
                    res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state'])
                    this.phaseList = res.data.list
                })
            }
        },
        getList () {
            this.list = [
                { scoringMethod: '打分项', evaluationItems: '成本合理性', evaluationDetails: '价格竞争力', divide: '100', weight: '20', scoring: '100', score: '20', remark: '' },
                { scoringMethod: '打分项', evaluationItems: '成本改进能力', evaluationDetails: '成本改进能力', divide: '100', weight: '20', scoring: '100', score: '20', remark: '' },
                { scoringMethod: '打分项', evaluationItems: '日常生产供货规范', evaluationDetails: '订单反馈确认', divide: '100', weight: '20', scoring: '100', score: '20', remark: '' },
                { scoringMethod: '打分项', evaluationItems: '日常生产供货规范', evaluationDetails: '到货及时准确性', divide: '100', weight: '20', scoring: '100', score: '20', remark: '' },
            ]
        }
    }
}
