import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { mapState } from "vuex"

export default {
    data () {
        let validateMoney = (rule, value, callback) => {
            const reg = /(^[1-9]\d{0,7}$)/
            if (value == '' || value == undefined || value == null) {
                callback()
            } else {
                if ((!reg.test(value)) && value != '') {
                    callback(new Error('请输入正确的金额'))
                } else {
                    callback()
                }
            }
        }
        let validatePercentage = (rule, value, callback) => {
            const reg = /^(100|[1-9]?\d(\.\d\d?\d?)?)$|0$/
            if (value == '' || value == undefined || value == null) {
                callback()
            } else {
                if ((!reg.test(value)) && value != '') {
                    callback(new Error('请输入正确的百分比'))
                } else {
                    callback()
                }
            }
        }
        return {
            dialogStatus: '',
            dialogVisible: false,
            temp: {},
            rules: {
                yield: [
                    { validator: validateMoney, trigger: "blur", message: '请输入首位不为0的1-8位数' }
                ],
                outputValue: [
                    { validator: validateMoney, trigger: "blur", message: '请输入首位不为0的1-8位数' }
                ],
                supplyProportion: [
                    { validator: validatePercentage, trigger: "blur" }
                ],
                defectRate: [
                    { validator: validatePercentage, trigger: "blur" }
                ],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdq: state => state.dict.dt_srm_pdq,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdq"])
    },
    methods: {
        create () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecordproduct(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('getFreshList')
                    })
                } else {
                    return false
                }
            })
        },
        update () {
            api.updateRecordproduct(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        },
        resetTemp () {
            this.temp = {
                name: undefined,
                level: undefined,
                customerName: undefined,
                productModelName: undefined,
                factoryName: undefined,
                yield: undefined,
                outputValue: undefined,
                supplyVolume: undefined,
                supplyProportion: undefined,
                productCapacity: undefined,
                defectRate: undefined,
                supplierId: this.temp.supplierId
            }
        }
    }
}
