import * as api from "@/api/project/srm/priceManage/purchasePriceInquiry"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'

export default {
    name: "wlxz",
    components: { Pagination },
    props: ['unMutiple', 'title', 'isRequired', 'orgCode', 'supplierCode', 'validTimeStart', 'validTimeEnd'],
    data () {
        return {
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            listQuery: {
                orgCode: undefined,
                supplierCode: undefined,
                sectionTimeStart: undefined,
                sectionTimeEnd: undefined,
                page: true,
                currentPage: 1,
                pageSize: 10,
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            dialogVisible: false
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdv: state => state.dict.dt_srm_pdv,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_pdv'])
    },
    watch: {
        orgCode: {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.getList()
            },
            deep: true
        },
        supplierCode: {
            handler (newValue) {
                this.listQuery.supplierCode = newValue
                this.getList()
            },
            deep: true
        },
        validTimeStart: {
            handler (newValue) {
                this.listQuery.sectionTimeStart = newValue
                this.getList()
            },
            deep: true
        },
        validTimeEnd: {
            handler (newValue) {
                this.listQuery.sectionTimeEnd = newValue
                this.getList()
            },
            deep: true
        },
    },
    methods: {
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.materielCode === items.materielCode) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        } else {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        }
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                }
                this.dialogVisible = false
            }
        },
    }
};
