import * as api from '@/api/project/ems/onlineMonitoring/online.js'
// import Bar from '@/views/project/EMS/onlineMonitoring/processMonitoring/temp/bar.vue'
export default {
    name: 'processMonitoring', //工序监控与预警
    // components: { Bar },
    data () {
        return {
            theight: 0, //表格高度
            list: [],
            isMore: false,
            loading: true,
            listQuery: {
                warningName: 'productProcessWarning' // workShopWarning
            },
            remark: [
                { class: 'fault', info: '水' },
                { class: 'standby', info: '电' },
                { class: 'run', info: '气' }
            ],
            setTimeoutValue: null
        }
    },
    computed: {
        holder () {
            return this.list.length % 6 == 0 ? 0 : 6 - (this.list.length % 6)
        }
    },
    mounted () {
        this.getList()
    },
    destroyed () {
        this.clear()
    },
    methods: {
        showMore () {
            this.isMore = !this.isMore
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.query(this.listQuery).then(response => {
                this.list = response.data
                this.loading = false
            })
        },
        setTimeout () {
            this.setTimeoutValue = setInterval(() => {
                this.getList()
            }, 60000 * 5)
        },
        clear () {
            if (this.setTimeoutValue) {
                clearInterval(this.setTimeoutValue)
            }
        }
    }
}
