import * as api from '@/api/project/mes/dataBase/work-center'
import Pagination from '@/components/Pagination'

export default {
    name: 'workCenter',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            DT_ORG_TYPE: 'dt_org_type_factory',
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: undefined,
                workCenterCode: undefined,
                workCenterName: undefined,
                parentCode: undefined,
                workCenterClass: undefined
            },
            orgCode: undefined,
            parentCode: undefined,
            parentName: undefined,
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0
        }
    },
    computed: {},
    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList() {
            this.listQuery.workCenterClass = 'dt_workcenter_class_line'
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getList()
        },

        // 获取表格选中时的数据
        selectRow(val) {
            this.selectedRows = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb.setCurrentRow(this.selectedRows)
            this.$emit('fresh', this.selectedRows)
        }
    }
}
