import { getDeviceDate } from "@/api/project/iot/MOM/deviceQuery"
import { queryCurve } from "@/api/project/iot/IOT/db"
import { mapState } from 'vuex'
export default {
    name: "DBTimeData",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        }
    },
    computed: {
        ...mapState({
            iot_state: state => state.iotDict.iot_state,
        })
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            tableKey: 0,//表格索引
            listQuery: {
                pointEndTime: undefined,
                pointStartTime: undefined
            },
            dialogFormVisible: false,
            pointCode: undefined,
            chartList: null
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        },
            this.getList()
    },
    methods: {
        stateClass (state) {
            if (state == '0') {
                return 'iot-warning'
            } else if (state == '1') {
                return 'iot-success'
            } else if (state == '2') {
                return 'iot-info'
            }
        },
        //获取表格数据
        getList () {
            this.listLoading = true
            getDeviceDate(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(response => {
                this.list = response.data
                let options = [this.iot_state]
                this.list = this.$myFun.srmCodeToName(
                    response.data,
                    options,
                    ['deliveryState']
                )
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 查看曲线
        handleViewCurves (pointCode) {
            //获取当前时间
            var day = new Date()
            var now = new Date()
            day.setTime(day.getTime() - 2 * 60 * 60 * 1000)
            this.listQuery.pointStartTime = getDate(day)
            this.listQuery.pointEndTime = getDate(now)
            function getDate (day) {
                var year = day.getFullYear()//得到年份
                var month = day.getMonth() + 1 > 10 ? day.getMonth() + 1 : '0' + (day.getMonth() + 1)//得到月份
                var date = day.getDate() > 10 ? day.getDate() : '0' + day.getDate()//得到日期
                var hour = day.getHours() > 10 ? day.getHours() : '0' + day.getHours()//得到小时
                var minu = day.getMinutes() > 10 ? day.getMinutes() : '0' + day.getMinutes()//得到分钟
                var sec = day.getSeconds() > 10 ? day.getSeconds() : '0' + day.getSeconds()//得到秒
                return `${year}-${month}-${date} ${hour}:${minu}:${sec}`
            }
            this.pointCode = pointCode
            this.dialogFormVisible = true
            this.handleQuery()
        },
        // 搜索曲线
        handleQuery () {
            queryCurve(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.pointCode, this.listQuery).then(res => {
                this.chartList = res.data
                this.drawLine()
            })
        },
        drawLine () {
            let xAxisData = this.chartList.map(item => {
                return item.pointTime
            })
            let data = this.chartList.map(item => {
                return {
                    value: item.pointValue,
                    name: item.pointName,
                }
            })
            // 基于准备好的dom，初始化echarts实例
            let myChart = this.$echarts.init(this.$refs.myChart)
            // 绘制图表
            myChart.setOption({
                title: { text: '' },
                tooltip: {
                    formatter: '{b}<br/>{c}'
                },
                xAxis: {
                    data: xAxisData
                },
                yAxis: {},
                series: [
                    {
                        name: '',
                        type: 'line',
                        data: data
                    }
                ]
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() + 30
        },
    }
};
