import * as api from "@/api/project/srm/supplierSelection/RFQconfigure"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
export default {
    components: { caiGou },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            dialogVisible: false,
            temp: {
                rowId: undefined
            },
            rules: {
                purchaseOrg: [{ required: true, message: '采购组织不能为空', trigger: 'blur' }],
                businessType: [{ required: true, message: '业务类型不能为空', trigger: 'blur' }],
                attachmentType: [{ required: true, message: '下发附件类型不能为空', trigger: 'blur' }],
                contentExplain: [{ required: true, message: '下发内容说明不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdf: state => state.dict.dt_srm_pdf,
            dt_srm_pdg: state => state.dict.dt_srm_pdg,
            dt_srm_pdh: state => state.dict.dt_srm_pdh,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdf", 'dt_srm_pdg', 'dt_srm_pdh'])

    },
    methods: {
        //获取组织编码名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //新增确定按钮单击事件
        createData () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })

        },
        //编辑确定按钮单击事件
        updateData () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        },
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                businessType: undefined,
                attachmentType: undefined,
                contentExplain: undefined,
                replyAttachmentType: undefined,
                attribute: undefined,
                reviewNoticeType: undefined,
                remark: undefined
            }
        }
    }
}
