export default {
    name: 'tabYCLcost',
    props: ['tabYCLcostList'],
    data () {
        return {
            type: null,
            tableData: [],
        }
    },
    watch: {
        tabYCLcostList (val) {
            if (this.type == 'add') {
                let arr = JSON.parse(JSON.stringify(val))
                arr.forEach(i => {
                    i.actuarialUnitPrice = i.unitPrice ? i.unitPrice : 0 //单价精算
                    i.actuarialConsumptionQuota = i.consumptionQuota ? i.consumptionQuota : 0 //定额精算
                    i.actuarialNetWeight = i.netWeight ? i.netWeight : 0 //净重精算
                    i.actuarialRecoveredUnitPrice = i.recoveredUnitPrice ? i.recoveredUnitPrice : 0 //回收单价精算
                    i.actuarialRejectRate = i.rejectRate ? i.rejectRate : 0 //废品率价精算

                    i.actuarialSubtotalCost = i.actuarialUnitPrice * i.actuarialConsumptionQuota || 0
                    i.actuarialRecyclableWeight = (i.actuarialConsumptionQuota - i.actuarialNetWeight) * 0.98 || 0
                    i.actuarialRecoveryValue = i.actuarialRecyclableWeight * i.actuarialRecoveredUnitPrice || 0
                    i.actuarialWasteLoss = i.actuarialUnitPrice * i.actuarialNetWeight * i.actuarialRejectRate || 0
                })
                this.tableData = arr
            } else {
                let arr = JSON.parse(JSON.stringify(val))
                this.tableData = arr
            }

        }
    },
    methods: {
        checkValue (val) {
            if (isNaN(val) || val == Infinity) {
                return undefined
            } else {
                if (val.toString().indexOf('.') !== -1) {
                    val = parseFloat(val).toFixed(2)
                }
                return val
            }
        },
        getSummaries (param) {
            const { columns, data } = param

            const sums = []

            console.log(data, 'columns')
            columns.forEach((column, index) => {
                if (index === 0) {
                    sums[index] = '总计'
                    return
                }
                const values = data.map(item => Number(item[column.property]))
                if (!values.every(value => isNaN(value))) {
                    if (column.property == 'subpartName' || column.property == 'actuarialUnitPrice' || column.property == 'actuarialConsumptionQuota' || column.property == 'actuarialSubtotalCost' || column.property == 'actuarialSubtotalCost' || column.property == 'actuarialRecyclableWeight' || column.property == 'actuarialRecoveredUnitPrice' || column.property == 'actuarialRecoveryValue' || column.property == 'actuarialRejectRate' || column.property == 'actuarialWasteLoss') {
                        sums[index] = values.reduce((prev, curr) => {
                            const value = Number(curr)
                            if (!isNaN(value)) {
                                return prev + curr
                            } else {
                                return prev
                            }
                        }, 0)
                    } else {
                        sums[index] = '--'
                    }
                    // sums[index] += ' 元'
                } else {
                    sums[index] = 0
                }
            })
            columns.forEach((val, index) => {
                //小计精算
                if (val.property == 'actuarialSubtotalCost') {
                    this.$store.dispatch('getData/getData', sums[index])
                }
                //回收价值精算
                if (val.property == 'actuarialRecoveryValue') {
                    this.$store.dispatch('getData/getDataRecoveryValue', sums[index])
                }
                //废品损失精算
                if (val.property == 'actuarialWasteLoss') {
                    this.$store.dispatch('getData/getDataWasteLoss', sums[index])
                }
            })
            return sums
        },

        //计算小计
        getPriceCost (row) {
            this.tableData.forEach(i => {
                if (i.rowId == row.rowId) {
                    i.actuarialSubtotalCost = this.checkValue(i.actuarialUnitPrice * i.actuarialConsumptionQuota)
                    i.actuarialRecyclableWeight = this.checkValue((i.actuarialConsumptionQuota - i.actuarialNetWeight) * 0.98)
                    i.actuarialRecoveryValue = this.checkValue(i.actuarialRecyclableWeight * i.actuarialRecoveredUnitPrice)
                    i.actuarialWasteLoss = this.checkValue(i.actuarialUnitPrice * i.actuarialNetWeight * i.actuarialRejectRate)
                    console.log(i.rowId, i.actuarialWasteLoss, 'mmmm')
                }
            })
        },
        initData (val) {
            this.type = val
            this.tableData = []
        }
    }
}
