import * as api from "@/api/project/srm/supplierSelect/optNotice/optNoticeFormation"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import Pagination from "@/components/Pagination"
import dialogImport from '@/components/Dialog/dialogImport'
import dialogAdd from './dialog/dialogAdd'
import dialogRefplan from './dialog/dialogRefplan'
import upload from '@/components/Dialog/upload'//附件上传
import examine from "./dialog/examine" //方案审核

export default {
    name: "optNoticeFormation",
    components: { caiGou, Pagination, dialogAdd, dialogRefplan, dialogImport, upload, examine },
    data () {
        return {
            functionCode: null,
            fileType: null,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            itemList: [],//从表数据
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: null,
                noticeNo: null,
                supplierName: null,
                materialCode: null,
                state: null,
                startDate: null,
                endDate: null
            },
            itemQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                noticeNo: null
            },
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '选择通知书', prop: 'noticeNo', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '物料编码', prop: 'materialCode', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_p0', label: '状态', prop: 'state', width: '', class: 'filter-item ml_5' },
                { type: 'daterange', label: '创建时间', prop: 'par9', width: '250px', startpl: '开始时间', endpl: '结束时间', class: 'filter-item ml_5' },
            ],
            oprateBtnConfig: [
                { id: 0, class: 'filter-item', icon: 'el-icon-plus', clickMethod: 'handleAdd', name: '新增' },
                { id: 1, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleRefPlan', name: '参照方案' },
                { id: 2, class: 'filter-item ml_5', icon: 'el-icon-edit-outline', clickMethod: 'handlePublish', name: '发布' },
                { id: 3, class: 'filter-item ml_5', icon: 'el-icon-upload2', clickMethod: 'handleImport', name: '资料上传' },
                { id: 4, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleAudit', name: '审批流程示意图' },
            ],
            tableConfig: [
                { label: '状态', prop: 'statusCN', align: 'center', width: 'auto' },
                { label: '通知书编号', prop: 'noticeNo', align: 'center', width: 'auto' },
                { label: '主要内容', prop: 'content', align: 'center', width: 'auto' },
                { label: '业务类型', prop: 'businessType', align: 'center', width: 'auto' },
                { label: '采购组织', prop: 'orgName', align: 'center', width: 'auto' },
                { label: '供应商编码', prop: 'supplierCode', align: 'center', width: '140' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: 'auto' },
                { label: '编制单位', prop: 'createOrgCode', align: 'center', width: '180' },
                { label: '编制人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '编制时间', prop: 'createTime', align: 'center', width: 'auto' },
                { label: '备注', prop: 'remark', align: 'center', width: 'auto' }
            ],
            tableConfigSub: [
                { label: '物料编码', prop: 'materialCode', align: 'center', width: '180' },
                { label: '物料名称', prop: 'materialName', align: 'center', width: '180' },
                { label: '物料分类', prop: 'materialClassName', align: 'center', width: '180' },
                { label: '特殊区分', prop: 'specialDistinction', align: 'center', width: '180' },
                { label: '材质', prop: 'materialQuality', align: 'center', width: '180' },
                { label: '重量', prop: 'weight', align: 'center', width: '180' },
                { label: '项目号', prop: 'projectNo', align: 'center', width: 'auto' },
                { label: '选择方案', prop: 'planNo', align: 'center', width: 'auto' },
                { label: '选择方式', prop: 'choiceMethod', align: 'center', width: 'auto' },
                { label: '信息来源', prop: 'sourceType', align: 'center', width: '140' }
            ],
            dialogConfig: [
                { titleA: '选择通知书新增', title: '选择通知书编辑', curdialogStatus: 'add', unMutiple: false },
                { title: '上传附件' },
                { title: '参照方案' }
            ],
            modalNo: 'EXTP-BAS-0001',//导入模板编号,
            dialogImportVisible: false
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,//状态
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0"])

        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.startDate = value[0]
                this.listQuery.endDate = value[1]
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 100
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0]
                res.data = this.$myFun.transCodeToName(res.data, options, ['status'])
                this.dataList = res.data.list
                this.total = res.data.list.length
                this.setFirstLine()
            })
        },
        //单条数据获取
        getMaterial () {
            api.queryMaterial(this.currentSelectedRow.noticeNo).then(res => {
                // let options = [this.dt_srm_pad]
                // res.data.proposalItemDTOs = this.$myFun.codeToName(res.data.proposalItemDTOs, options, [
                //     'position'
                // ])
                this.itemList = res.data.list
                this.totalItem = res.data.list.length
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //操作按钮事件
        oprateClick (id) {
            switch (id) {
                case 0: {
                    this.handleAdd()
                    break
                }
                case 1: {
                    this.handleRefPlan()
                    break
                }
                case 2: {
                    this.handlePublish()
                    break
                }
                case 3: {
                    this.handleImport()
                    break
                }
                case 4: {
                    this.handleAudit()
                    break
                }

            }
        },
        //新增按钮
        handleAdd () {
            this.dialogConfig[0].curdialogStatus = 'add'
            this.$refs.refadd.dialogVisible = true
            this.$refs.refadd.initForm('add', this.listQuery)
        },
        handleRefPlan () {
            this.$refs.refPlan.initForm('type', 'row')
            this.$refs.refPlan.tableData = []
            this.$refs.refPlan.resetTemp()
            this.$refs.refPlan.dialogVisible = true
        },
        //导入按钮
        handleImport () {
            if (this.currentSelectedRow) {
                this.functionCode = this.currentSelectedRow.noticeNo
                this.fileType = 'document'
                this.$nextTick(() => {
                    this.$refs.upload.getList()
                    this.$refs.upload.dialogVisible = true
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //提交按钮
        submit () {
            if (this.currentSelectedRow) {
                this.$confirm(`您确定提交 提案号：${this.currentSelectedRow.proposalNo} 的审批流程吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    // api.publish(this.currentSelectedRow.proposalNo).then(() => {
                    //     this.$message({
                    //         type: 'success',
                    //         message: '发布成功'
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //审核按钮单击事件
        handleExamine () {
            if (this.currentSelectedRow) {
                this.$refs.examine.dialogVisible = true
                this.$refs.examine.temp.rowId = this.currentSelectedRow.rowId
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //审批流程示意图
        handleAudit () {
            this.$message({ type: 'error', message: '开发中，暂无法使用' })
        },
        //发布按钮
        handlePublish () {
            if (this.currentSelectedRow) {
                this.$confirm(`您确定启动 通知书编号：${this.currentSelectedRow.noticeNo} 的审批流程吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    let cdata = {
                        method: "RELEASE",
                        selectNoticeDTO: this.selectedRows[0]
                    }
                    api.updateRecords(cdata.selectNoticeDTO.noticeNo, cdata).then(() => {
                        this.$message({ type: 'success', message: '发布成功' })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //表格操作编辑
        handleUpdate (row) {
            api.queryRow(row.noticeNo).then(res => {
                this.dialogConfig[0].curdialogStatus = 'edit'
                this.$refs.refadd.dialogVisible = true
                this.$refs.refadd.isEdit = true
                this.$refs.refadd.initForm('edit', res.data)
            })
        },
        handleSubmit (row) {
            let cdata = {
                method: "SUBMIT",
                selectNoticeDTO: row
            }
            api.updateRecords(cdata.selectNoticeDTO.noticeNo, cdata).then(() => {
                this.$message({ type: 'success', message: '提交成功' })
                this.getList()
            })
        },
        //表格操作删除
        handleDelete (row) {
            this.$confirm(`确定删除 通知书编号${this.currentSelectedRow.noticeNo} 吗？`, '消息提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
                api.deleteRecords(row.noticeNo).then(() => {
                    this.$message({
                        type: 'success',
                        message: '删除成功'
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: '已取消'
                })
            })

        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                    this.getMaterial()
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                    // this.itemList = []
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
                this.getMaterial()
            }

        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                if (this.dataList.length > 0) {
                    this.$refs.tb.toggleRowSelection(this.dataList[0], "selected")
                    this.$refs.tb.setCurrentRow(this.dataList[0])
                } else {
                    this.itemList = []
                }
            })
        },
        //确认添加/编辑
        confirmAddEdit (cdata) {
            if (this.dialogConfig[0].curdialogStatus === 'add') {
                //添加api post
                api.createRecords(cdata).then(() => {
                    this.$message({ type: 'success', message: '新增成功' })
                    this.getList()

                })
            } else if (this.dialogConfig[0].curdialogStatus === 'edit') {
                //编辑api patch
                api.updateRecords(cdata.selectNoticeDTO.noticeNo, cdata).then(() => {
                    this.$message({ type: 'success', message: '编辑成功' })
                    this.getList()
                })
            }
        }
    }
};
