import { mapState } from "vuex"
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { queryRecords } from "@/api/project/srm/supplierManage/supplierAccess/platformInProposal"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from "@/utils/constant"
// import { mapState } from 'vuex'


export default {
    name: "ghxl",
    components: { Pagination, caiGou },
    props: ['unMutiple', 'title', 'selDisabled'],
    data () {
        return {
            list: null,
            listSelected: [],
            dialogVisible: false,
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                orgCode: null,
                proposalNo: undefined,
                supplierCode: undefined,
                supplierName: undefined
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '提案号', prop: 'proposalNo', width: '', class: 'filter-item ml_5' },
                // { type: 'commonInput', label: '供应商编码', prop: 'supplierCode', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' }
            ],
            tableConfig: [
                // { label: '序号', prop: 'num', align: 'center', width: 'auto' },
                { label: '提案号', prop: 'proposalNo', align: 'center', width: '180' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: '180' },
                { label: '物料分类', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '平台系列', prop: 'platformSeriesCN', align: 'center', width: 'auto' },
                { label: '平台定位', prop: 'positionCN', align: 'center', width: 'auto' },
                { label: '提案时间', prop: 'updateTime', align: 'center', width: '140' }//wu
            ],
        }
    },
    computed: {
        ...mapState({
            dt_cate_platformSeries: state => state.srmBisSelect.dt_cate_platformSeries,//平台系列
            dt_srm_pad: state => state.dict.dt_srm_pad
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pad"])
        this.$store.dispatch("srmBisSelect/getBisData", ["dt_cate_platformSeries"])
        this.getList()
    },
    methods: {
        initSelect (val) {
            this.list = null
            this.listSelected = null
            this.listSelected = val
            this.getList()
        },
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            queryRecords(this.listQuery).then(res => {
                let options = [this.dt_cate_platformSeries.data, this.dt_srm_pad]
                res.data = this.$myFun.transCodeToName(res.data, options, [['platformSeries', 'code', 'name'], 'position'])
                res = res.data
                this.list = res.list
                this.list.map(item => {
                    item.supplierName = this.listQuery.supplierName
                })
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.proposalNo === items.proposalNo) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.proposalNo === row.proposalNo) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        isSelectable (row) {
            let senable = true
            this.listSelected.forEach((item) => {
                if (item.proposalNo === row.proposalNo && item.rowId) {
                    senable = false
                }
            })
            return senable
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.proposalNo === items.proposalNo) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.proposalNo !== row.proposalNo
                    })
                }
            }
        },
        //选择确认
        handleConfirm () {
            this.$emit('handleDialogClick', this.listSelected)
            this.dialogVisible = false
        }
    }
};
