import { mapState, mapGetters } from "vuex"
import * as api from "@/api/project/srm/supplierManage/supplierAccess/reviewFeedback"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件

// import * as api from "@/api/system/role"
import Pagination from "@/components/Pagination"
import dialogFeedback from './dialog/dialogFeedback'
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory'//物料分类选择




// import { mapState } from "vuex"
export default {
    name: "reviewFeedback",
    components: { caiGou, Pagination, dialogFeedback, dialogPersonSelect, dialogMaterielCategory },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: null,
                investigateNo: null,
                materielClass: null,
                supplierName: null,
                planSource: null,
                taskUser: null,
                taskUserName: null,
                startDate: null,
                endDate: null
            },
            itemQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                evaluationNo: null
            },
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '考察计划号', prop: 'investigateNo', width: '', class: 'filter-item ml_5' },
                { type: 'inputBtnMC', label: '物料分类', prop: 'materielClassName', width: '', class: 'ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_p0', label: '状态', prop: 'state', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pai', label: '计划来源', prop: 'planSource', width: '', optionArr: [], class: 'filter-item ml_5' },
                { type: 'inputBtnPerson', label: '接收人', prop: 'taskUserName', copmNum: 1, class: 'ml_5' },//wu
                { type: 'daterange', label: '考察时间段', prop: 'par9', width: '250px', startpl: '考察时间', endpl: '结束时间', class: 'filter-item' },
            ],
            tableConfig: [
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '计划来源', prop: 'planSourceCN', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierName', align: 'center', width: '180' },
                { label: '物料分类', prop: 'materielClassName', align: 'center', width: '180' },
                { label: '采购组织', prop: 'orgName', align: 'center', width: 'auto' },
                { label: '考察计划', prop: 'investigateNo', align: 'center', width: '150' },
                { label: '考察部门', prop: 'department', align: 'center', width: 'auto' },
                { label: '考察部门名称', prop: 'departmentName', align: 'center', width: 'auto' },
                { label: '考察项', prop: 'itemCN', align: 'center', width: 'auto' },
                { label: '是否考察', prop: 'investigate', align: 'center', width: 'auto' },
                { label: '理由', prop: 'reason', align: 'center', width: 'auto' },
                { label: '考察人', prop: 'investigateUser', align: 'center', width: 'auto' },
                { label: '评价人', prop: 'evaluateUserName', align: 'center', width: 'auto' }
            ],
            dialogConfig: [
                { title: '考察反馈计划' },
                { title: '接收人选择', unMutiple: true, propCode: 'taskUser', propName: 'taskUserName', required: false },
                { title: '选择物料分类', isRequired: false, unMutiple: true }
            ]
        }
    },
    created () { },
    computed: {
        ...mapGetters([
            'domainCode',
            'domainCodeName'
        ]),
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,//状态
            dt_srm_pai: state => state.dict.dt_srm_pai,
            dt_srm_pb4: state => state.dict.dt_srm_pb4,//考察项
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", "dt_srm_pai", "dt_srm_pb4"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.setDefault()
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.startDate = value[0]
                this.listQuery.endDate = value[1]
            }
        },
        'domainCode' (value) {
            this.$set(this.listQuery, 'taskUser', value)
        }
    },
    methods: {
        //设置默认值
        setDefault () {
            this.$set(this.listQuery, 'taskUser', this.domainCode)
            this.$set(this.listQuery, 'taskUserName', this.domainCodeName)
            this.$set(this.listQuery, 'state', 'P002')
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.queryFeedback(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pai, this.dt_srm_pb4]
                res.data = this.$myFun.codeToName(res.data, options, ['state', 'planSource', 'item'])
                this.dataList = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //表格操作编辑
        handleFeedback (index, row) {
            console.log('反馈', index, row)
            api.queryProjectItem(row.investigateNo, row.rowId).then(res => {
                console.log('queryProjectItem', res)
                this.$refs.refFeedback.dialogVisible = true
                this.$refs.refFeedback.isEdit = true
                this.$refs.refFeedback.initForm(res.data)
            })
        },
        //确认添加/编辑
        confirmFeedback (cdata) {
            api.updateProjectRecords(cdata.feedbackItemDTO.investigateNo, cdata.feedbackItemDTO.rowId, cdata).then(res => {
                console.log('updateProjectRecords', res)
                this.$message({ type: 'success', message: '反馈提交成功' })
                this.getList()
            })
        },
        showPerson (propCode, propName, title) {
            this.dialogConfig[1].title = title
            this.dialogConfig[1].propCode = propCode
            this.dialogConfig[1].propName = propName
            this.$refs.refPerson.dialogVisible = true
        },
        personConfirm (val, propCode, propName) {
            if (propCode === 'taskUser') {
                this.$set(this.listQuery, propCode, val[0].employeeCode)
                this.$set(this.listQuery, propName, val[0].employeeName)
            }
        },
        showMC () {
            if (!this.listQuery.orgCode) {
                this.$message({ type: 'warning', message: '请先选择组织编码' })
            } else {
                this.$refs.refMC.listQuery.orgCode = this.listQuery.orgCode
                this.$refs.refMC.handleQuery()
                this.$refs.refMC.dialogVisible = true
            }
        },
        handleConfirmMC (data) {
            this.listQuery.materielClass = data[0].classCode
            this.$set(this.listQuery, 'materielClassName', data[0].className)
        }
        //清空接收人@blur="clearTaskUser"
        // clearTaskUser (val) {
        //     if (this.listQuery.taskUserName !== this.domainCodeName) {
        //         this.$set(this.listQuery, 'taskUser', null)
        //         this.$set(this.listQuery, 'taskUserName', null)
        //     }
        // }
    }
};
