import { queryAllList as getWorkStation } from '@/api/project/eas/database/work-center'
export default {
    name: 'workStationSelect',
    props: {
        disabled: {
            type: Boolean,
            default: false
        },
        placeholder: {
            type: String,
            default: '请选择'
        }
    },
    model: {
        prop: 'workCenterCode',
        event: 'change'
    },
    data() {
        return {
            options: [],
            itemKey: this.stationCode
        }
    },
    watch: {
        workCenterCode() {
            this.itemKey = this.stationCode
        }
    },
    created() {
        this.getOptions()
    },
    methods: {
        getOptions() {
            getWorkStation().then(res => {
                this.options = res.data
            })
        },
        changeSelect(val) {
            this.$emit('change', val)
            let name = ''
            let org = this.options
                .filter(v => v.stationCode == val)
                .find(value => value)
            if (org) {
                name = org.stationName
            }

            this.$emit('selectName', name)
        }
    }
}
