import { queryDeviceAlarm } from "@/api/project/iot/IOT/db"
import Pagination from "@/components/Pagination"
export default {
    name: "commandRecord",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        }
    },
    components: { Pagination },
    watch: {
        itemData (val) {
            if (val) {
                this.getList()
            }
        }
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            list: [],
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            queryDeviceAlarm(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.listQuery).then(response => {
                this.list = response.data.list
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 支付
        pay () { },
        // 查看曲线
        handleViewCurves () { },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 20
        },
    }
};
