import * as api from "@/api/project/srm/supplierSelect/technicalReview"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import addMain from "./dialog/addMain"
// import { getIssuedAt } from '@/utils/auth'
import moment from 'moment'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
export default {
    name: "technicalReview",
    components: { Pagination, addMain, caiGou },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            timer: "",//定义一个定时器的变量
            nowDate: "",    // 当前日期
            nowTime: "",    // 当前时间
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            totalSub: 0,
            time: undefined,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                technicalProposal: '1',
                orgCode: 'dtcg0001',
                rfqType: undefined
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_pdj: state => state.dict.dt_srm_pdj,
            dt_srm_pdg: state => state.dict.dt_srm_pdg,
            dt_srm_pag: state => state.dict.dt_srm_pag,
        }),
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdj", 'dt_srm_pdg', 'dt_srm_pag'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
        this.getServerTime()

    },
    watch: {
        time (val) {
            setTimeout(() => {
                this.time = moment(val)
                    .add(1, 's')
                    .format('YYYY-MM-DD HH:mm:ss')
            }, 1000)
        }
    },
    methods: {
        timeFormat () {
            var _this = this
            let yy = new Date().getFullYear()
            let mm = new Date().getMonth() + 1
            let dd = new Date().getDate()

            let hh = new Date().getHours()
            let mf =
                new Date().getMinutes() < 10
                    ? "0" + new Date().getMinutes()
                    : new Date().getMinutes()
            let ss =
                new Date().getSeconds() < 10
                    ? "0" + new Date().getSeconds()
                    : new Date().getSeconds()
            _this.nowTime = hh + ":" + mf + ":" + ss
            _this.nowDate = yy + "-" + mm + "-" + dd
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        getServerTime () {
            api.serverTime().then(res => {
                this.time = res.data
            })
        },
        getFreshList () {
            this.getList()
            this.getSubList()
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //显示主表
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pdj]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['technicalEvaluationState'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        //显示从表
        getSubList () {
            console.log(this.selectlistRow, 'kkkk')
            let dto = {
                rfqNo: this.selectlistRow.rfqNo,
                audit: '1',
                rfqType: this.listQuery.rfqType,
                materielCode: this.listQuery.materielCode
            }
            api.queryRecordItems(dto).then(res => {
                let options = [this.dt_srm_pag]
                res.data = this.$myFun.srmCodeToName(res.data, options, ['evaluationConclusion'])
                this.listSub = res.data
                this.totalSub = res.data.length
            })
        },
        //评审提交
        handleSubmit () {
            if (this.selectlistRow) {
                if (this.selectlistRow.technicalEvaluationState === 'PDJ5') {
                    let dto = {
                        method: 'SUBMIT_TECHNICAL_EVALUATION',
                        rfqNo: this.selectlistRow.rfqNo
                    }
                    api.submit(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '提交成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                        this.listSub = []
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '只有全部评审的数据才可提交',
                        type: 'warning',
                        duration: 2000
                    })
                }

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //评审
        handleReview () {
            if (this.selectlistRowSub && this.selectlistRowSub.length > 0) {
                this.$refs.addMain.resetTemp()
                this.$refs.addMain.dialogVisible = true
                this.$refs.addMain.list = this.selectlistRowSub
                this.$refs.addMain.temp.rfqNo = this.selectlistRowSub[0].rfqNo
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChangeSub (val) {
            this.selectlistRowSub = val
        },
        //点击表格某一行
        rowClickSub (val) {
            this.$refs.tbSub.toggleRowSelection(val)
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        }
    }
};
