export default {
    name: "gauge",
    props: {
        value: {
            type: Number,
            default: 0
        },
        echartsKey: {
            type: String,
            default: undefined
        },
        comp: {
            type: String,
            default: undefined
        },
        name: {
            type: String,
            default: undefined
        },
        listLoading: {
            type: Boolean,
            default: false
        }
    },
    watch: {
        listLoading (val) {
            if (val) {
                this.drawLine()
            }
        }
    },
    mounted () {
        this.drawLine()
        let _this = this
        window.addEventListener('resize', function () {
            if (_this.myChart) {
                _this.myChart.resize()
            }
        })
    },
    data () {
        return {
            height: 280,
            myChart: null
        }
    },
    methods: {
        drawLine () {
            // 基于准备好的dom，初始化echarts实例
            this.myChart = this.$echarts.init(document.getElementById(this.echartsKey))
            // 绘制图表
            let option = {
                tooltip: {
                    confine: true,
                    formatter: `{a} <br/>{b} : {c} ${this.comp}`
                },
                series: [
                    {
                        name: this.name,
                        type: 'gauge',
                        center: ['50%', '55%'],
                        radius: '80%',
                        title: {
                            fontSize: 12
                        },
                        detail: {
                            textStyle: {
                                fontSize: 20,
                            },
                            formatter: '{value}' + this.comp
                        },
                        data: [{
                            value: this.value, name: this.name
                        }]
                    }
                ]
            }
            this.myChart.setOption(option, true)
        },
    },
};
