import * as api from "@/api/project/spm/sparePartsPurchase/requirementsGathering"
import { DICT_CODE } from '@/utils/constant'
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import setDivision from "../dialog/setDivision.vue"
import orgSelect from '@/components/Select/orgSelect'
export default {
    name: "division",
    components: { Pagination, orgSelect, setDivision },
    data () {
        return {
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                state: 'srss10'
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({

        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {

    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecordsDivision(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //分工按钮单击事件
        handleDivision () {
            //新增按钮单击事件方法
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                this.$refs.setDivision.resetTemp()
                this.selectlistRow.forEach((val, index) => {
                    this.$refs.setDivision.rowIds[index] = val.rowId
                })
                this.$refs.setDivision.dialogVisible = true
            } else {
                this.$message({
                    title: "警告",
                    message: "请勾选记录",
                    type: "warning"
                })
            }
        },
        //发布按钮单击事件
        handlePublish () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let status = this.selectlistRow.every(v => {
                    return v.planerName
                })
                console.log(status)
                if (status) {
                    this.$confirm('此操作将该信息进行发布, 是否继续?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }).then(() => {
                        let rowIds = []
                        this.selectlistRow.forEach((val, index) => {
                            rowIds[index] = val.rowId
                        })
                        api.publish(rowIds).then(() => {
                            this.$message({
                                title: '成功',
                                message: '发布成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                        })
                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消发布'
                        })
                    })
                } else {
                    this.$message({
                        title: "警告",
                        message: "已分工才能发布",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: "警告",
                    message: "请勾选记录",
                    type: "warning"
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
