import * as api from "@/api/project/srm/quotaManagement/quotaApplication"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
export default {
    name: "wlxz",
    components: { Pagination, caiGou, dialogGysxz },
    props: ['unMutiple', 'title', 'isRequired'],
    data () {
        return {
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                materielCode: undefined,//物料编码
                materielName: undefined,//物料名称
                materielType: undefined,//类型
                materielClassify: undefined,//分类
                materielGroup: undefined//物料组
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            dialogVisible: false
        }
    },
    computed: {
        ...mapState({
            dt_unit_type: state => state.dict.dt_unit_type,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_unit_type"])
    },
    methods: {
        checked (i) {
            console.log(i, 'mmmm')
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            api.querymaterialRecords(this.listQuery).then(res => {
                let options = [this.dt_unit_type]
                res.data = this.$myFun.codeToName(res.data, options, ['basicUmo'])
                res = res.data
                this.list = res.list.map(v => {
                    v.groupNo = v.groupCode
                    v.groupNoName = v.groupName
                    return v
                })
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if ((item.groupNo.toString() + item.materialCode.toString() + item.orgCode.toString()) === (items.groupNo.toString() + items.materialCode.toString() + items.orgCode.toString())) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if ((item.groupNo.toString() + item.materialCode.toString() + item.orgCode.toString()) === (row.groupNo.toString() + row.materialCode.toString() + row.orgCode.toString())) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if ((item.groupNo.toString() + item.materialCode.toString() + item.orgCode.toString()) === (items.groupNo.toString() + items.materialCode.toString() + items.orgCode.toString())) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return (item.groupNo.toString() + item.materialCode.toString() + item.orgCode.toString()) !== (row.groupNo.toString() + row.materialCode.toString() + row.orgCode.toString())
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleConfirm', [{ materialCode: null, materialName: null }])
                        } else {
                            this.$emit('handleConfirm', [{ materialCode: null, materialName: null }])
                        }
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materialCode: null, materialName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                }
                this.dialogVisible = false
            }
        },
    }
};
