import * as api2 from '@/api/data-base/work-center'
import { recommend } from '@/api/project/wms/purchaseStorage/purchaseReceiving'
import { queryRecords } from '@/api/data-base/org-materiel'
import { getSelectedDetail } from "@/api/data-base/bar_code_rules"
import { selectlists } from '@/api/system/organization'
import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/productStocking/finishedStocking"
import { mapState } from "vuex"
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import wlxzbyzsj from "@/components/Select/wlxzbyzsj" //主数据物料信息模块
import qrcode from "@/components/QRCode"
import JsBarcode from 'jsbarcode'


export default {
    name: "finishedStocking",
    components: {        Pagination,
        AuthoType,
        wlxzbyzsj,
        qrcode    },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            workCenterCode_options: [],
            options: [],
            options1: [],
            areaList: null,
            packDataList: null,
            updatePacklist: {
                barCode: [],
                method: '',
                supplierBatch: '',
                supplierRemark: ''
            },
            locatorList: [],
            list: [],
            list1: null,
            list2: null,
            total: 0,
            total1: 0,
            total2: 0,
            activeName2: "first",
            isEdit: false,
            disabled: false,
            accountDate: [this.currentTime(), this.currentTime()],
            createTime: [this.currentTime(), this.currentTime()],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: undefined,
                arrivalOrgCode: "",
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: this.timeFormat(),
                toWarehouseCode: undefined,
                workshopCode: undefined
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                workshopCode: undefined
            },
            listQueryPack: {
                orderNumber: '',
                materielCode: '',
                orderNumbers: [],
                barcodeRuleDetails: []
            },
            storesTransfersDTO: {
                accountDate: undefined,
                businessType: undefined,
                businessTypeName: undefined,
                orgCode: undefined,
                receiveOrderItemDTOs: [],
                receiveOrderNumber: undefined,
                sourceOrderNumber: undefined,
                supplierCode: undefined,
                supplierName: undefined,
                thirdWarehouseCode: undefined,
                warehouseCode: undefined,
                warehouseName: undefined
            },
            temp: {
                orgCode: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: undefined,
                toWarehouseCode: undefined
            },
            giftQuantity: undefined,
            materielCode: undefined,
            measuringUnit: undefined,
            receiveQuantity: undefined,
            snp: undefined,
            sourceRowNo: undefined,
            specification: undefined,
            wareAreaCode: undefined,
            wareLocationCode: undefined,
            receiveOrderNumber: undefined,
            sourceOrderNumber: undefined,
            supplierCode: undefined,
            locationCode: undefined,
            productBatch: undefined,
            proofNo: undefined,
            warehouseCode: undefined,
            orgCode: undefined,
            parentsRows: null,
            listQuery2: {
                receiveOrderNumber: undefined,
                proofNo: undefined,
                currentPage: 1,
                pageSize: 10
            },
            printData: {},//打印数据
            packPrintList: [],
            dialogPack: false,//包装标签tag
            dialogPackSet: false,//包装标签设置
            dialogPackPrintVisible: false,//打印弹框显示
            dto: {
                detailDTOS: [],
                dicType: undefined,
                dicTypeName: undefined,
                sourceType: undefined,
                sourceTypeName: undefined,
            },
            dtos: {
                businessType: undefined,
                orgCode: undefined,
                wareMaterielDTOS: [],
                warehouseCode: undefined
            },
            selectPackRows: [],
            value1: "",
            dialogStatus: "",
            wlxzRows: null,
            wlxzDialogFormVisible: false,
            tableKey: 0,
            businessTypeName: undefined,
            dialogPrintVisible: false,
            rules: {
                contact: [
                    { max: 32, message: this.$t('validate.max32'), trigger: 'blur' }
                ],
                mobile: [
                    { max: 32, message: this.$t('validate.max32'), trigger: 'blur' }
                ],
                plateNumber: [
                    { max: 32, message: this.$t('validate.max32'), trigger: 'blur' }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
            dt_work_center_workshop: state => state.businessComponent.dt_work_center_workshop,
            dt_business_type_screenGroupNum: state => state.businessComponent.dt_business_type_screenGroupNum
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("dict/getDicData", [
            "dt_materiel_type",
            "dt_materiel_classify"
        ])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_work_center_workshop",
            "dt_business_type_screenGroupNum"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 30
            this.pheight = this.$myFun.getSingleTbHeight() + 56
            this.sheight = this.$myFun.getSingleTbHeight() - 70
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        currentTime () {
            var date = new Date()
            var seperator1 = "-"
            var year = date.getFullYear()
            var month = date.getMonth() + 1
            var strDate = date.getDate()
            if (month >= 1 && month <= 9) {
                month = "0" + month
            }
            if (strDate >= 0 && strDate <= 9) {
                strDate = "0" + strDate
            }
            var currentdate = year + seperator1 + month + seperator1 + strDate
            return currentdate
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.toWarehouseCode = val
                this.listQuery.businessType = this.dt_business_type_screenGroupNum.options[0].wareRuleCode
                this.changeOrg()
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQuery1.orgCode = val
            } else if (i === 3) {
                this.listQuery1.warehouseCode = val
                this.listQuery1.businessType = this.dt_business_type_screenGroupNum.options[0].wareRuleCode
                this.stateWorkshop()
            }
        },
        changeOrg () {//组织编码下拉change
            this.listQuery1.orgCode = this.listQuery.orgCode
            api2.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.listQuery.workshopCode = res.list[0].bufferArea
            })

        },

        stateWorkshop () {
            api2.queryRecords(this.listQuery1).then(res => {
                res = res.data
                this.listQuery1.workshopCode = res.list[0].bufferArea
            })

        },
        changeWarehouseCode () {//仓库编码下拉change
            this.listQuery1.warehouseCode = this.listQuery.warehouseCode
            this.listQuery.toWarehouseCode = this.listQuery.warehouseCode
        },
        changeWorkshopCode () {//车间下拉change
            this.listQuery1.workshopCode = this.listQuery.workshopCode
        },
        getList1 () {//查询凭证号
            if (this.accountDate) {
                this.listQuery1.accountDateStart = this.accountDate[0]
                this.listQuery1.accountDateEnd = this.accountDate[1]
            } else {
                this.listQuery1.accountDateStart = ""
                this.listQuery1.accountDateEnd = ""

            }
            if (this.createTime) {
                this.listQuery1.createDateStart = this.createTime[0]
                this.listQuery1.createDateEnd = this.createTime[1]
            } else {
                this.listQuery1.createDateStart = ""
                this.listQuery1.createDateEnd = ""

            }
            this.list2 = []
            selectlists().then(response => {
                api.queryRecords1(this.listQuery1).then(res => {
                    res = res.data
                    res.list.forEach((item, index) => {
                        response.data.forEach((option) => {
                            if (option.organizationCode == item['orgCode']) {
                                res.list[index]['orgCode' + 'CN'] = option.organizationName
                            }
                        })

                    })
                    this.list1 = res.list
                    this.total = res.pages.count
                })
            })

        },
        getList2 () {//已选项查询
            const _this = this
            api.queryRecords(this.listQuery2).then(res => {
                res = res.data
                res.list.map(v => {
                    v.businessTypeName = _this.businessTypeName
                    v.workshopCode = _this.workshopCode
                    return v
                })
                this.list2 = res.list
                this.total = res.pages.count
            })
        },

        rowClassName ({ row, rowIndex }) {
            //自增序号
            row.id = rowIndex + 1
        },
        selectRow1 (val) {
            this.selectRowData1 = val
        },
        //查询包装标签列表
        handleQueryPack () {

        },
        handleCreate () {//新增弹窗
            if (this.listQuery.orgCode && this.listQuery.accountDate && this.listQuery.warehouseCode && this.listQuery.businessType && this.listQuery.toWarehouseCode) {
                this.wlxzDialogFormVisible = true
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.list]
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择查询条件",
                    type: "warning"
                })
            }
            api.getArea(this.listQuery.warehouseCode).then(resa => {
                resa = resa.data
                this.areaList = resa
            })
        },
        handleDblclickWlxz (row) {
            this.wlxzRows = row
        },
        handleUpdate () {
            let result = true
            let groupList = []
            this.list.forEach((item) => {
                if (!item.areaCode || !item.locationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    })
                    result = true
                }
                this.list.map(v => {
                    v.wareAreaCode = v.areaCode
                    v.wareLocationCode = v.locationCode
                    v.toWareAreaCode = '*'
                    v.toWareLocationCode = '*'
                    return v


                })
                if (item.receiveQuantity > 0) {
                    groupList.push(item)
                    result = false
                    this.storesTransfersDTO.receiveOrderItemDTOs = groupList
                    if (result) {
                        this.$message({
                            title: "警告",
                            message: "本次发货不可全部为0或空",
                            type: "warning"
                        })
                    }
                }

            })
            this.storesTransfersDTO.orgCode = this.listQuery.orgCode
            this.storesTransfersDTO.businessType = this.listQuery.businessType
            this.storesTransfersDTO.accountDate = this.listQuery.accountDate
            this.storesTransfersDTO.warehouseCode = this.listQuery.warehouseCode
            this.storesTransfersDTO.supplierCode = this.listQuery.workshopCode
            this.storesTransfersDTO.receiveOrderItemDTOs = groupList
            api.addRecords(this.storesTransfersDTO).then(() => {
                this.disabled = true
                this.$message({
                    title: "成功",
                    message: "保存成功",
                    type: "success",
                    duration: 2000
                })
            })

        },

        update () {
            if (this.$refs.wlxzRef.listSelected.length > 0) {
                this.list = this.$refs.wlxzRef.listSelected
                this.wlxzDialogFormVisible = false
                this.isEdit = true
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        handleDelete () {//删除列表
            if (this.selectRowData1 && this.selectRowData1.length == 1) {
                this.$confirm("此操作将删除所选中数据, 是否继续?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                })
                    .then(() => {
                        for (let i = 0; i < this.list.length; i++) {
                            if (
                                this.list[i].materielCode ===
                                this.selectRowData1[0].materielCode
                            ) {
                                this.list.splice(i, 1)
                                this.$message({
                                    title: "成功",
                                    message: "删除成功",
                                    type: "success",
                                    duration: 2000
                                })
                            }
                        }
                    })
                    .catch(() => {
                        this.$message({
                            type: "info",
                            message: "已取消删除"
                        })
                    })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handelRefresh () {//刷新
            this.isEdit = false
            this.disabled = false
            this.list = []
            // this.listQuery = [];
        },
        handleRecommend () {
            if (this.selectRowData1) {
                this.dtos.businessType = this.listQuery.businessType,
                    this.dtos.orgCode = this.listQuery.orgCode,
                    this.dtos.wareMaterielDTOS = this.selectRowData1
                this.dtos.warehouseCode = this.listQuery.warehouseCode,
                    recommend(this.dtos).then(res => {
                        res = res.data
                        res.forEach((val) => {
                            this.list.forEach((val1, index) => {
                                if (val.materielCode == val1.materielCode) {
                                    this.$set(this.list[index], 'areaCode', val.areaCode)
                                    this.$set(this.list[index], 'locationCode', val.locationCode)
                                    this.$set(this.list, index, this.list[index])
                                }
                            })
                        })
                    })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        selectRowchange (val) {
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }

            if (this.selectlistRowchange) {
                this.listQuery2.receiveOrderNumber = this.selectlistRowchange.receiveOrderNumber
                this.businessTypeName = this.selectlistRowchange.businessTypeName
                this.workshopCode = this.selectlistRowchange.workshopCode
                this.getList2()
            }

        },
        //打印
        receiptPrint () {
            if (this.selectlistRowchange) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowchange
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.receiveOrderNumber)
                })
            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }
        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)

        },
        handleDownload () {//导出
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '业务类型', '物料编码', '物料名称',
                    '数量', '生产批次', '车间', '组织编码',
                    '仓库编码', '记账日期', '创建日期', '记账人',
                ]
                const filterVal = [
                    'receiveOrderNumber', 'businessTypeName', 'materielCode', 'materielName',
                    'receiveQuantity', 'productBatch', 'supplierCode', 'orgCode',
                    'warehouseCode', 'accountDate', 'createTime', 'creator',
                ]
                const data = this.formatJson(filterVal, this.list2)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        handleFilter1 () {
            if (this.listQuery1.businessType) {
                this.getList1()
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择查询条件",
                    type: "warning"
                })
            }
        },
        clickRow (val) {//凭证号行点击
            this.clickListRow = val
            this.$refs.tbb.clearSelection()
            this.$refs.tbb.toggleRowSelection(val) //单击行绑定点击事件
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.locatorList, i, res)
                this.$set(this.list[i], 'locationCode', res[0].locationCode)
                this.$set(this.list, i, this.list[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.listQuery.warehouseCode, areaCode, i)
        },
        //包装标签点击事件
        packTag () {
            if (this.selectlistRowchange) {
                this.dialogPack = true
                this.dto.detailDTOS = []
                queryRecords(this.listQuery).then(resr => {
                    resr = resr.data
                    let obj = {
                        arrivalCompany: this.selectlistRowchange.orgCode,
                        arrivalCompanyName: this.selectlistRowchange.orgCodeCN,
                        arrivalRepository: this.selectlistRowchange.warehouseCode,
                        arrivalRepositoryName: this.selectlistRowchange.warehouseName,
                        orderNumber: this.selectlistRowchange.receiveOrderNumber,
                        supplierCode: this.selectlistRowchange.supplierCode,
                        supplierName: this.selectlistRowchange.supplierName,
                        orderRowNo: this.selectlistRowchange.id,
                    }
                    this.list2.forEach((item, index) => {
                        this.dto.detailDTOS.push(obj)
                        for (let key in item) {
                            if (key === "receiveQuantity") {
                                this.dto.detailDTOS[index]["receiveNum"] = item[key]
                            } else {
                                this.dto.detailDTOS[index][key] = item[key]
                            }
                        }
                        resr.list.forEach((items) => {
                            if (items.materielCode === item.materielCode) {
                                this.dto.detailDTOS[index]["snp"] = items.stockContain
                            }
                        })
                    })
                    this.dto.dicType = 'LB04'
                    this.dto.dicTypeName = '成品'
                    this.dto.sourceType = 'source_type02'
                    this.dto.sourceTypeName = '凭证'
                    api.createPackTag(this.dto).then(() => {
                        let rulesQuery = {
                            ruleType: this.dto.dicType,
                            arrivalCompany: this.selectlistRowchange.orgCode
                        }
                        getSelectedDetail(rulesQuery).then(respon => {
                            let codearr = []
                            respon.barcodeRuleDetails.forEach((ritem) => {
                                codearr.push(ritem.rulePrepCode)
                            })

                            let orderNumbers = []
                            orderNumbers.push(this.selectlistRowchange.receiveOrderNumber)
                            this.listQueryPack.orderNumbers = orderNumbers
                            this.listQueryPack.barcodeRuleDetails = codearr
                            api.queryPackTag(this.listQueryPack).then(resq => {
                                this.packDataList = resq
                            })

                        })
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条凭证号",
                    type: "warning"
                })
            }
        },
        //选中标签列表
        selectPackRow (val) {
            this.selectPackRows = val
        },

        //批量打印
        handleBulkPrint () {
            if (this.selectPackRows.length === 0) {
                this.$message({
                    title: this.$t('message.warning'),
                    message: '请选择要打印的标签',
                    type: "warning"
                })
            } else {
                let printable = true
                try {
                    this.selectPackRows.forEach(item => {
                        if (item.printSign === 1) {
                            printable = false
                            this.$message({
                                title: this.$t('message.warning'),
                                message: '所选项包含已打印过的标签，不能进行批量打印',
                                type: "warning"
                            })
                            throw ''
                        }
                    })
                    if (printable) {
                        this.updateMethod = 'batchPrint'
                        this.createPrintPack()
                    }
                } catch (e) {
                    console.log(e)
                }
            }
        },
        //补打
        handleFillPrint () {
            if (this.selectPackRows.length === 0) {
                this.$message({
                    title: this.$t('message.warning'),
                    message: '请选择要打印的标签',
                    type: "warning"
                })
            } else {
                let printable = true
                try {
                    this.selectPackRows.forEach(item => {
                        if (item.printSign === 0) {
                            printable = false
                            this.$message({
                                title: this.$t('message.warning'),
                                message: '所选项包含未打印过的标签，不能进行补打',
                                type: "warning"
                            })
                        }
                    })
                    if (printable) {
                        this.updateMethod = 'makeUpPrint'
                        this.createPrintPack()
                    }
                } catch (e) {
                    console.log(e)
                }
            }

        },
        initUpdatePacklist () {
            let barcodes = new Array()
            this.selectPackRows.forEach(item => {
                barcodes.push(item.barCode)
            })
            this.updatePacklist.barCode = barcodes
            this.updatePacklist.method = this.updateMethod
            if (this.updateMethod !== 'setUp') {
                this.updatePacklist.supplierBatch = ''
                this.updatePacklist.supplierRemark = ''
            }
        },
        //生成打印内容
        createPrintPack () {
            this.dialogPackPrintVisible = true
            this.packPrintList = this.selectPackRows
            this.$nextTick(() => {
                this.packPrintList.forEach(item => {
                    this.$refs['childQRcode' + item.rowId][0].qrCreate(item.qrCode)
                    JsBarcode("#barcode" + item.rowId, item.barCode)
                })
            })
        },
        //标签设置确认提交
        confirmPackBatch () {
            this.updateMethod = 'setUp'
            this.initUpdatePacklist()
            api.updatePackTag(this.updatePacklist).then(() => {
                api.queryPackTag(this.listQueryPack).then(resq => {
                    this.packDataList = resq
                })
                this.$message.success('设置成功')
            })
        },
        //执行包装标签打印
        doPackPrint () {
            this.$confirm('是否进行打印操作？', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning',
            }).then(() => {
                this.dialogPackPrintVisible = false
                this.initUpdatePacklist()
                api.updatePackTag(this.updatePacklist).then(() => {
                    api.queryPackTag(this.listQueryPack).then(resq => {
                        this.packDataList = resq
                    })
                    this.$message.success('设置成功')
                })
                let newWin = window.open("")
                var newstr = document.getElementById('packprint').innerHTML
                newWin.document.write(newstr)
                newWin.document.close()//IE添加
                setTimeout(function () {
                    newWin.print()
                    newWin.close()
                }, 100)
            })
        }
    }
};
