import * as api from "@/api/project/srm/supplierManage/supplierAccess/accessEvaluate"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import Pagination from "@/components/Pagination"
import dialogAudit from './dialog/dialogAudit'
import dialogResult from './dialog/dialogResult'
import dialogDetail from './dialog/dialogDetail'

export default {
    name: "comprehensiveEvaluation",
    components: { caiGou, Pagination, dialogResult, dialogDetail, dialogAudit },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            itemList: [],//从表数据
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: null,
                supplierName: null,
                state: null,
                evaluationNo: null
            },
            // itemQuery: {
            //     page: true,
            //     currentPage: 1,
            //     pageSize: 10,
            //     evaluationNo: null
            // },
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_p0', label: '状态', prop: 'state', width: '', optionArr: [], class: 'filter-item ml_5' },
                { type: 'commonInput', label: '评价单号', prop: 'evaluationNo', width: '', class: 'filter-item ml_5' }
            ],
            oprateBtnConfig: [
                { id: 1, class: 'filter-item ml_5', icon: 'el-icon-finished', clickMethod: 'handleSubmit', name: '提交' },
                { id: 2, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleConclude', name: '会议结论' },
                { id: 3, class: 'filter-item ml_5', icon: 'el-icon-edit-outline', clickMethod: 'handlePublish', name: '发布' },
            ],
            tableConfig: [
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '来源', prop: 'planSourceCN', align: 'center', width: 'auto' },
                { label: '物料分类', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '车型平台', prop: 'platformSeriesCN', align: 'center', width: 'auto' },
                { label: '定位', prop: 'positionCN', align: 'center', width: 'auto' },
                { label: '评价单编号', prop: 'evaluationNo', align: 'center', width: '160' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: '180' },
                { label: '评价分数', prop: 'score', align: 'center', width: 'auto' },
                { label: '评价等级', prop: 'grade', align: 'center', width: 'auto' },
                { label: '评价结论', prop: 'auditResultCN', align: 'center', width: 'auto' },
                { label: '考察计划号', prop: 'investigateNo', align: 'center', width: '180' },
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: '150' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: '150' }
            ],
            tableConfigSub: [
                { label: '评价单号', prop: 'evaluationNo', align: 'center', width: '160' },
                { label: '分项评价', prop: 'evaluationItemCN', align: 'center', width: 'auto' },
                { label: '评价分数', prop: 'score', align: 'center', width: 'auto' },
                { label: '评价等级', prop: 'grade', align: 'center', width: 'auto' },
                { label: '推荐意见', prop: 'recommendResult', align: 'center', width: 'auto' },
                { label: '评价结论', prop: 'auditResultCN', align: 'center', width: 'auto' },
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },//dt_srm_pal
                { label: '评价人', prop: 'evaluationUserName', align: 'center', width: 'auto' },
                { label: '提交日期', prop: 'submitDate', align: 'center', width: 'auto' },
                { label: '考察人', prop: 'investigateUser', align: 'center', width: 'auto' },
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: '150' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: '150' }
            ],
            dialogConfig: [
                { title: '评价结果维护', unMutiple: false },
                { title: '评价类型明细查看' },
                { title: '附件清单' },
            ],
            modalNo: 'EXTP-BAS-0001',//导入模板编号,
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_pb4: state => state.dict.dt_srm_pb4,//考察项
            dt_srm_p0: state => state.dict.dt_srm_p0,//单据状态
            dt_srm_pag: state => state.dict.dt_srm_pag,//审核结论
            dt_cate_platformSeries: state => state.srmBisSelect.dt_cate_platformSeries,//平台系列
            dt_srm_pad: state => state.dict.dt_srm_pad,//平台定位
            dt_srm_pal: state => state.dict.dt_srm_pal,//评价状态
            dt_srm_pai: state => state.dict.dt_srm_pai,//计划来源
            dt_srm_pam: state => state.dict.dt_srm_pam//综合评价等级
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pb4", "dt_srm_p0", "dt_srm_pag", "dt_srm_pad", "dt_srm_pal", "dt_srm_pai", "dt_srm_pam"])
        this.$store.dispatch("srmBisSelect/getBisData", ["dt_cate_platformSeries"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_cate_platformSeries.data, this.dt_srm_pad, this.dt_srm_pai, this.dt_srm_pam, this.dt_srm_pag]
                res.data = this.$myFun.transCodeToName(res.data, options, [
                    'state', ['platformSeries', 'code', 'name'], 'position', 'planSource', 'grade', 'auditResult'
                ])
                this.dataList = res.data.list
                this.total = res.data.pages.count
                this.setFirstLine()
            })
        },
        //单条数据获取
        getRow () {
            api.queryRow(this.currentSelectedRow.evaluationNo).then(res => {
                let options = [this.dt_srm_pal, this.dt_srm_pb4]
                res.data.evaluationItemDTOs = this.$myFun.codeToName(res.data.evaluationItemDTOs, options, [
                    'state', 'evaluationItem'
                ])
                this.itemList = res.data.evaluationItemDTOs
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //操作按钮事件
        oprateClick (id) {
            switch (id) {
                case 1: {
                    this.handleSubmit()
                    break
                }
                case 2: {
                    this.handleConclude()
                    break
                }
                case 3: {
                    this.handlePublish()
                    break
                }
            }
        },
        //提交
        handleSubmit () {
            if (this.currentSelectedRow) {
                console.log('发布')
                this.$confirm(`您确定提交 评价：${this.currentSelectedRow.evaluationNo} 吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    api.updateRecords(this.currentSelectedRow.evaluationNo, { method: "SUBMIT" }).then(() => {
                        this.$message({
                            type: 'success',
                            message: '提交成功'
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //会议结论
        handleConclude () {
            if (this.currentSelectedRow) {
                this.$refs.refAudit.auditResultArr = this.dt_srm_pag
                this.$refs.refAudit.auditResult = this.dt_srm_pag[0].dictItemKey
                this.$refs.refAudit.evaluationNo = this.currentSelectedRow.evaluationNo
                this.$refs.refAudit.dialogVisible = true
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }
            // this.$message({ type: 'error', message: '开发中，暂无法使用' })
        },
        //发布按钮
        handlePublish () {
            if (this.currentSelectedRow) {
                console.log('发布')
                this.$confirm(`您确定发布 评价：${this.currentSelectedRow.evaluationNo} 吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    api.updateRecords(this.currentSelectedRow.evaluationNo, { method: "RELEASE" }).then(() => {
                        this.$message({
                            type: 'success',
                            message: '发布成功'
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }
        },
        //结果维护
        handleResult (index, row) {
            console.log('结果维护', index, row)
            api.queryRow(row.evaluationNo).then(res => {
                let options = [this.dt_srm_pam, this.dt_srm_pal, this.dt_srm_pag]
                res.data.evaluationItemDTOs = this.$myFun.codeToName(res.data.evaluationItemDTOs, options, [
                    'grade', 'state', 'auditResult'
                ])
                this.$refs.refResult.dialogVisible = true
                this.$refs.refResult.initForm(res.data)
            })
        },
        //查看
        handleView (row) {
            api.queryRowItem(row.evaluationNo, row.rowId).then(res => {
                this.$refs.refDetail.dialogVisible = true
                this.$refs.refDetail.formData.supplierCode = this.selectedRows[0].supplierCode
                this.$refs.refDetail.formData.supplierName = this.selectedRows[0].supplierName
                this.$refs.refDetail.initForm(res.data)
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                    this.getRow()
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                    // this.itemList = []
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
                this.getRow()
            }
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tb.toggleRowSelection(this.dataList[0], "selected")
                this.$refs.tb.setCurrentRow(this.dataList[0])
            })
        },
        //确认结果维护
        confirmResult (evaluationNo, cdata) {
            api.updateRecords(evaluationNo, cdata).then(() => {
                this.$message({ type: 'success', message: '操作成功' })
                this.getList()
            })
        },
        //审核结论提交
        handleConfirmAudit (evaluationNo, data) {
            api.updateRecords(evaluationNo, data).then(() => {
                this.$refs.refAudit.dialogVisible = false
                this.$message({ type: 'success', message: '操作成功' })
                this.getList()
            })
        }
    }
};
