import { mapState } from "vuex"
import Pagination from '@/components/Pagination' // 分页组件
import { queryRecords } from "@/api/project/srm/supplierManagement/supplierRegister"
export default {
    components: { Pagination },
    //是否多选，名称，是否必须选择
    props: ['unMutiple', 'isRequired', 'title', 'stateFilter'],
    data () {
        return {
            valueType: 'code',//code返回code name,row返回一行
            listAll: null,//备选项
            listSelected: [],//已选项
            dialogVisible: false,
            totalAll: 0,//总条数
            totalSelected: 0,//选中的条数 暂未使用
            activeName: 'first',//tab激活内容
            tableKey: [0, 1, 2],//表格索引
            currentSelectedRow: null,
            checkProp: ['rowId', 'rowId'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                isExist: null,
                supplierCode: null,
                supplierName: null,
                classification: '',
                platformState: 'PA1P,PA1Q',
                materielClass: null,
                materielClassName: null
            },//备选项查询条件
            filterConfig: [
                { type: 'commonInput', label: '供应商编码', prop: 'supplierCode', width: '', class: 'filter-item' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pa3', label: '供应商分类', prop: 'classification', width: '', optionArr: [], class: 'filter-item ml_5' },
                { type: 'commonInputWl', label: '物料分类', prop: 'materielClassName', width: '', class: 'filter-item ml_5' },
                // { type: 'dictSelect', dType: 'dt_srm_pa1', label: '供应商类型', prop: 'platformState', width: '', optionArr: [], class: 'filter-item ml_5' }
            ],
            mainTitle: [
                { width: null, align: 'left', label: '状态', prop: 'platformStateCN' },
                { width: null, align: 'left', label: '系统编码', prop: 'rowId' },
                { width: null, align: 'left', label: '供应商编码', prop: 'supplierCode' },
                { width: '180', align: 'left', label: '供应商名称', prop: 'supplierName' },
                { width: null, align: 'left', label: '供应商分类', prop: 'classificationCN' },//dt_srm_pa3
                { width: '180', align: 'left', label: '注册地址', prop: 'registeredAddress' },
                { width: null, align: 'left', label: '供应商简称', prop: 'supplierShortName' },
                { width: null, align: 'left', label: '联系电话', prop: 'telephone' }
            ]//备选项主表头数据
        }
    },
    computed: {
        ...mapState({
            dt_srm_pa1: state => state.dict.dt_srm_pa1,
            dt_srm_pa3: state => state.dict.dt_srm_pa3
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pa1", "dt_srm_pa3"])//创建时加载备选项
        this.getList()
    },
    methods: {
        //初始化选中内容
        initSelect (val) {
            if (this.unMutiple === true && !val) {
                if (!this.listAll) {
                    this.getList()
                }
            } else {
                this.listSelected = val
                this.getList()
            }
        },
        //选中某一行
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.listAll[i], true)
        },
        handleQuery () {//查询备选项
            this.getList()
        },
        getList () {//备选项查询
            queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pa1, this.dt_srm_pa3]
                res.data = this.$myFun.codeToName(res.data, options, [
                    'platformState', 'classification'
                ])
                if (this.stateFilter == true) {
                    this.listAll = res.data.list.filter(v => {
                        return v.platformState !== 'PA1Q'
                    })
                } else {
                    this.listAll = res.data.list
                }

                this.totalAll = res.data.pages.count

                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.listAll.forEach((item, index) => {
                            if (item[this.checkProp[0]] === items[this.checkProp[1]]) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.rowId === row.rowId) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        //row-click @row-click="selectRowMain"
        // selectRowMain (val) { //表一单击行选中方法
        //     if (this.unMutiple) {
        //         this.$refs.tbselect.clearSelection()
        //         this.$refs.tbselect.toggleRowSelection(val)
        //     } else {
        //         this.$refs.tbselect.toggleRowSelection(val)
        //     }
        // },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.rowId === items.rowId) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.rowId !== row.rowId
                    })
                }
            }
        },
        selectChange (val) {
            if (this.unMutiple) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.listSelected = val
                    this.currentSelectedRow = val[val.length - 1]
                } else if (val.length === 0) {
                    this.listSelected = val
                    this.currentSelectedRow = null
                }
            } else {
                this.listSelected = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleGysClick', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleGysClick', [{ supplierCode: null, supplierName: null }])
                        } else {
                            this.$emit('handleGysClick', null)
                        }
                    } else {
                        this.$emit('handleGysClick', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleGysClick', [{ supplierCode: null, supplierName: null }])
                    } else {
                        this.$emit('handleGysClick', null)
                    }
                }
                this.dialogVisible = false
            }
        }
    }
}
