import * as api from '@/api/project/wms/purchaseStorage/purchaseReceiving'
import { mapState } from 'vuex';
import caiGou from '@/components/Select/caigou.vue'//下拉组件

import Pagination from "@/components/Pagination";
import warehouseSelect from '@/components/Select/warehouse-select.vue'//下拉组件
import { DT_ORG_TYPE, DICT_CODE } from '@/utils/constant'

export default {
    name: "Hand_pulled_material",
    components: { Pagination, warehouseSelect, caiGou },
    data () {
        return {
            theight: 0,
            sheight: 0,
            tableKey: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list_a: [],
            total_a: 0,
            jareaList: null,
            jlocatorList: [],
            careaList: null,
            clocatorList: [],
            display: false,
            listQuery_a: {
                currentPage: 1,
                pageSize: 10,
                supplierCode: '',
                itemStates: undefined,
                accountDate: this.timeFormat()
            },
            selects: true,
            Edit: false,
            state: true,
            queryState: ['ADD', 'PART'],
            list_a_1: [],
            total_a_1: 0,
            listQuery_a_1: {
                currentPage: 1,
                pageSize: 10,
                itemStates: undefined
            },
            list_b: [],
            total_b: 0,
            releaseDateStart: [this.timeFormat(), this.timeFormat()],
            listQuery_b: {
                currentPage: 1,
                pageSize: 1000,
                supplierCode: ''
            },
            list_b_1: [],
            total_b_1: 0,
            listQuery_b_1: {
                currentPage: 1,
                pageSize: 10,
            },
            JHD: false,
            CG: false,
            name: undefined,
            activeName2: 'first',
            DT_ORG_TYPE: DT_ORG_TYPE,
            DICT_CODE: DICT_CODE,
            temp: {},
            dto: {
                businessType: undefined,
                orgCode: undefined,
                wareMaterielDTOS: undefined,
                warehouseCode: undefined
            },
            parentsRows: [],
            downloadLoading: false
        };
    },
    computed: {
        ...mapState({
            dt_materiel_transfer_type_screenGroupNum: state => state.businessComponent.dt_materiel_transfer_type_screenGroupNum
        })
    },
    mounted () {
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.$store.dispatch('businessComponent/getBusinessComponentData', ['dt_materiel_transfer_type_screenGroupNum']);
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() + 20;
            this.sheight = this.$myFun.getSingleTbHeight() - 40;
        },
        getList_b () {//收货凭证主表查询
            if (this.listQuery_a.arrivalCompany || this.listQuery_a.arrivalRepository || this.listQuery_a.businessType) {
                this.listQuery_b.orgCode = this.listQuery_a.arrivalCompany
                this.listQuery_b.warehouseCode = this.listQuery_a.arrivalRepository
                this.listQuery_b.businessType = this.listQuery_a.businessType
            }

            if (this.releaseDateStart) {
                this.listQuery_b.createDateStart = this.releaseDateStart[0]
                this.listQuery_b.createDateEnd = this.releaseDateStart[1]
            } else {
                this.listQuery_b.createDateStart = ""
                this.listQuery_b.createDateEnd = ""
            }

            if (this.listQuery_b.businessType == '0520') {
                this.JHD = true
                this.display = true
                api.getData_b(this.listQuery_b).then(res => {
                    res = res.data;
                    this.list_b = res.list
                    this.total_b = res.pages.count
                })
            } else {
                this.JHD = false
                this.display = false
                api.getData_b(this.listQuery_b).then(res => {
                    res = res.data;
                    this.list_b = res.list
                    this.total_b = res.pages.count
                })
            }
        },
        getList_b_1 (val) {//收货凭证从表查询
            this.listQuery_b_1.businessType = val.businessType
            this.listQuery_b_1.receiveOrderNumber = val.receiveOrderNumber
            api.getData_b_1(this.listQuery_b_1).then(res => {
                res = res.data;
                this.list_b_1 = res.list.map(v => {
                    for (const key in val) {
                        if (val.hasOwnProperty(key)) {
                            v[key] = val[key]
                        }
                    }
                    return v
                })
                this.total_b_1 = res.pages.count
            })
        },


        exportDataCG () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = ['凭证号', '业务类型', '物料编码', '物料名称', '数量', '供应商编码',
                    '供应商名称', '组织编码', '到货单位', '库区', '库位', '记账日期', '创建时间',
                    '记账人', '订单号', '订单行号'
                ]
                const filterVal = ['receiveOrderNumber', 'businessType', 'materielCode', 'materielName',
                    'receiveQuantity', 'supplierCode', 'supplierName', 'orgCode', 'warehouseCode',
                    'wareAreaCode', 'wareLocationCode', 'accountDate', 'createTime', 'creator',
                    'preSourceOrderNumber', 'preSourceRowNo'
                ]
                const data = this.formatJson(filterVal, this.list_b_1)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        exportDataJHD () {//导出
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = ['凭证号', '业务类型', '物料编码', '物料名称', '数量', '供应商编码',
                    '供应商名称', '组织编码', '到货单位', '库区', '库位', '记账日期', '创建时间',
                    '记账人', '订单号', '订单行号', '交货单号', '交货单行号'
                ]
                const filterVal = ['receiveOrderNumber', 'businessType', 'materielCode', 'materielName',
                    'receiveQuantity', 'supplierCode', 'supplierName', 'orgCode', 'warehouseCode',
                    'wareAreaCode', 'wareLocationCode', 'accountDate', 'createTime', 'creator',
                    'preSourceOrderNumber', 'preSourceRowNo', 'sourceOrderNumber', 'sourceRowNo'
                ]
                const data = this.formatJson(filterVal, this.list_b_1)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        selectRow_b (row) {//收货凭证主表
            this.getList_b_1(row)
        },
        selectRow_b_1 () {//收货凭证从表
        },
        savecheck () {
            let result = true;
            let b = this.list_a_1.filter(v => {
                if (v.receiveQuantity > 0 || v.RQuantit > 0) {
                    // v.measuringUnit=v.purchaseMeasuringUnit
                    v.sourceRowNo = v.rowNo;
                    v.deliveryRowNo = v.rowNo;
                    v.receiveRowNo = v.rowNo;
                    v.receiveQuantity = v.RQuantit;
                    v.toWareAreaCode = '*';
                    v.toWareLocationCode = '*';
                    return v
                }
            });
            b.forEach((v) => {
                if (!v.wareAreaCode || !v.wareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    });
                    result = false;
                }
            })
            // this.save=b
            if (result) {
                return b
            } else {
                return false;
            }

        },
        timeFormat () {
            var d = new Date();
            var year = d.getFullYear();       //年  
            var month = d.getMonth() + 1;     //月  
            var day = d.getDate();            //日  

            var clock = year + "-";
            if (month < 10)
                clock += "0";
            clock += month + "-";

            if (day < 10)
                clock += "0";
            clock += day;
            return (clock);
        },
        showGys () {
            this.$emit('showGys', 'proof');
        },
        setGys (rows) {
            this.parentsRows = rows;
            if (!this.parentsRows[0]) {
                this.$set(this.listQuery_b, 'supplierCode', '');
                this.$set(this.listQuery_b, 'supplierName', '');
            } else {
                this.$set(this.listQuery_b, 'supplierCode', this.supplierRows[0].supplierCode);
                this.$set(this.listQuery_b, 'supplierName', this.supplierRows[0].supplierName);
            }
        }
    }
}
