import * as api from "@/api/project/srm/priceManage/detailQuoteNote"
import dialogWlxzbyzsj from '@/components/Dialog/dialogWlxzbyzsj.vue'//主数据物料选择
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    components: { caiGou, dialogWlxzbyzsj, dialogGysxz },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            temp: {},
            dialogVisible: false,

        }
    },
    computed: {
        ...mapState({
            dt_srm_pdi: state => state.dict.dt_srm_pdi,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdi"])
    },
    methods: {
        //获取组织编码名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            this.$set(this.temp, 'materielCode', data[0].materialCode)
            this.$set(this.temp, 'materielName', data[0].materialName)
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.temp, 'supplierName', val[0].supplierName)
            this.$set(this.temp, 'supplierCode', val[0].supplierCode)
        },
        //编辑确定按钮单击事件
        updateData: throttle(function () {
            let dto = {
                quotationMethod: this.temp.quotationMethod,
                quotationRequirements: this.temp.quotationRequirements,
                remark: this.temp.remark,
                validDateEnd: this.temp.validDateEnd,
            }
            api.updateRecords(this.temp.rowId, dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        )
    }
}
