import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
import { queryRecords } from "@/api/project/iot/PM/equipmentProducts"

export default {
    name: 'deviceTypeSelect',
    components: { Pagination },
    props: {
        isTick: {
            type: Boolean,
            default: false //true 为多选
        },
        // 父组件中已选记录集合
        listSelected: {
            type: Array,
            default: () => {
                return []
            }
        },
    },
    computed: {
        ...mapState({
            iot_locked_industry: state => state.iotDict.iot_locked_industry,
            iot_trial_scenario: state => state.iotDict.iot_trial_scenario
        })
    },
    watch: {
        'listQuery1.industry' (val) {
            if (val) {
                this.getList1()
            }
        },
        'listQuery2.ctacle' (val) {
            if (val) {
                this.getList2()
            }
        }
    },
    data () {
        return {
            activeName: 'industry',
            theight: 0, //表格高度
            selectedRows1: null, //多选时选中的所有行
            selectlistRow1: undefined,//当前选择的
            selectedRows2: null, //多选时选中的所有行
            selectlistRow2: undefined,//当前选择的
            tableKey: [0, 1], //表格索引，虚拟dom渲染表格时使用
            list1: [],
            list2: [],
            total1: 0,
            total2: 0,
            listLoading1: true,
            listLoading2: true,
            listQuery1: {
                currentPage: 1,
                pageSize: 15,
                page: 1,
                industry: undefined
            },
            listQuery2: {
                currentPage: 1,
                pageSize: 15,
                page: 1,
                ctacle: undefined
            }
        }
    },
    created () {
        let p = new Promise((reject) => {
            reject(this.$store.dispatch("iotDict/getDicData", ["iot_locked_industry", "iot_trial_scenario"]))
        })
        p.then(() => {
            this.getList1()
            this.getList2()
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() * 3 / 5
        },
        getList1 () {
            //查询
            this.listLoading1 = true
            queryRecords(this.listQuery1).then(response => {
                this.list1 = response.data.list
                let options = [this.iot_locked_industry, this.iot_trial_scenario]
                this.list1 = this.$myFun.srmCodeToName(
                    this.list1,
                    options,
                    ['industry', 'ctacle']
                )
                this.total1 = response.data.pages.count
                setTimeout(() => {
                    this.listLoading1 = false
                }, 1.5 * 100)
            })
        },
        getList2 () {
            //查询
            this.listLoading2 = true
            queryRecords(this.listQuery2).then(response => {
                this.list2 = response.data.list
                let options = [this.iot_locked_industry, this.iot_trial_scenario]
                this.list2 = this.$myFun.srmCodeToName(
                    this.list2,
                    options,
                    ['industry', 'ctacle']
                )
                this.total2 = response.data.pages.count
                setTimeout(() => {
                    this.listLoading2 = false
                }, 1.5 * 100)
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange1 (val) {
            if (this.isTick) {
                this.selectedRows1 = val
                this.$emit('change', this.selectedRows1)
            } else {
                this.selectlistRow1 = val[val.length - 1]
                if (val.length > 1) {
                    this.$refs.tb1.clearSelection()
                    this.$refs.tb1.toggleRowSelection(val[val.length - 1])
                }
                this.$refs.tb1.setCurrentRow(this.selectlistRow1)
                this.$emit('change', this.selectlistRow1)
            }
        },
        selectChange2 (val) {
            if (this.isTick) {
                this.selectedRows2 = val
                this.$emit('change', this.selectedRows2)
            } else {
                this.selectlistRow2 = val[val.length - 1]
                if (val.length > 1) {
                    this.$refs.tb2.clearSelection()
                    this.$refs.tb2.toggleRowSelection(val[val.length - 1])
                }
                this.$refs.tb2.setCurrentRow(this.selectlistRow2)
                this.$emit('change', this.selectlistRow2)
            }
        }
    }
}
