import * as api from '@/api/project/eas/database/customer'
import Pagination from '@/components/Pagination'
import { TWO_STATE_OPTIONS } from '@/utils/constant'
import { validatorEmail } from '@/utils/validate' // validatorContactNumber,
import { mapState } from 'vuex'
import ImportFile from '@/components/ImportFile'
import { parseTime } from '@/utils'

export default {
    name: 'customer',
    components: { Pagination, ImportFile },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined
            },
            temp: {
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined,
                customerProperty: undefined,
                isRetailer: 0,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            },
            customerCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogFormVisible2: false,
            importDialogVisible: false, //导入弹框
            modalNo: 'EXTP-BAS-0001', //导入模板编号
            dialogStatus: '',
            tableKey: 0,
            isRetailerOptions: TWO_STATE_OPTIONS,
            rules: {
                customerCode: [
                    {
                        required: true,
                        message: '客户编码不能为空',
                        trigger: 'blur'
                    },
                    {
                        pattern: /^[A-Z-_0-9]{1,50}$/,
                        message: '由大写字母，数字，—和_组成，长度不能超过50',
                        trigger: 'blur'
                    }
                ],
                customerName: [
                    {
                        required: true,
                        message: '客户名称不能为空',
                        trigger: 'blur'
                    },
                    {
                        max: 100,
                        message: this.$t('validate.max32'),
                        trigger: 'blur'
                    }
                ],
                contact: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                contactNumber: [
                    {
                        pattern: /^0{0,1}(13[0-9]|15[7-9]|153|156|18[7-9])[0-9]{8}$/,
                        message: '手机号格式不对', trigger: 'blur'
                    } // validator: validatorContactNumber
                ],
                email: [{ validator: validatorEmail, trigger: 'blur' }],
                address: [
                    { max: 512, message: '长度不能超过512', trigger: 'blur' }
                ],
                remark: [
                    { max: 512, message: '描述长度为512内', trigger: 'blur' }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_customer_type: state => state.eamDict.dt_customer_type,
            dt_customer_property: state => state.eamDict.dt_customer_property
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', [
            'dt_customer_type',
            'dt_customer_property'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        selectRow (val) {
            this.selectlistRow = val
        },
        handleImport () {
            this.importDialogVisible = true
            this.$nextTick(() => {
                this.$refs.importDialog.getList()
            })
        },
        handleUpdate () {
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.readonly = true //组织编码不可以编写
                this.temp = this.selectlistRow[0] // copy obj
                api.getRecord(this.temp.customerCode).then(res => {
                    this.temp = res.data
                    this.dialogStatus = 'update'
                    this.dialogFormVisible = true
                    this.$nextTick(() => {
                        this.$refs['temp'].clearValidate()
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update () {
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.updateRecord(this.temp.customerCode, this.temp).then(
                        () => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        }
                    )
                }
            })
        },
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.customerCode
                        })
                        api.deleteRecord(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        create () {
            this.$refs.temp.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$notify({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                } else {
                    return false
                }
            })
        },
        resetTemp () {
            this.temp = {
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined,
                customerProperty: undefined,
                isRetailer: 0,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleExport () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '客户编码',
                    '客户名称',
                    '客户类型',
                    '客户属性',
                    '是否散户',
                    '联系人',
                    '联系电话',
                    '电子邮箱',
                    '通讯地址',
                    '描述'
                ]
                const filterVal = [
                    'customerCode',
                    'customerName',
                    'customerTypeName',
                    'customerPropertyName',
                    'isRetailer',
                    'contact',
                    'contactNumber',
                    'email',
                    'address',
                    'remark'
                ]
                const data = this.formatJson(filterVal, this.list)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v =>
                filterVal.map(j => {
                    if (j === 'timestamp') {
                        return parseTime(v[j])
                    } else {
                        return v[j]
                    }
                })
            )
        }
    }
}
