.login {
    width: 100%;
    height: 100%;
    background-color: #f0f4fa;
    display: flex;
    .content {
        display: flex;
        width: 50%;
        min-width: 1000px;
        // min-width: 1100px;
        // height: 60%;
        background-color: #fff;
        box-shadow: 0px 1px 32px 0px rgba(34, 45, 60, 0.05);
        border-radius: 10px;
        .content-left {
            width: 100%;
            // height: 760px;
            .bg {
                width: 100%;
                height: 100%;
                border-bottom-left-radius: 10px;
                border-top-left-radius: 10px;
            }

            .logo {
                position: absolute;
                margin-left: 30px;
                margin-top: 26px;
                width: 8%;
                min-width: 100px;
            }
        }

        .content-right {
            width: 100%;
            // display: flex;
            // justify-content: center;
            .login-box {
                margin: 0 auto;
                margin-top: 16%;
                width: 52%;
                .title-en {
                    font-size: 30px;
                    font-family: PingFang SC;
                    color: #212121;
                    opacity: 0.1;
                    border-bottom: 2px solid #dde2eb;
                    width: 100%;
                    padding-bottom: 5px;
                    padding-left: 60px;
                    padding-top: 10px;
                    margin-bottom: 40px;
                }
                .title {
                    position: absolute;
                    color: #212121;
                    font-size: 30px;
                    min-width: 130px;
                }
                .form {
                    display: flex;
                    justify-content: center;
                    width: 100%;
                    ::v-deep {
                        .el-input__inner {
                            width: 100%;
                            border-top-width: 0px;
                            border-left-width: 0px;
                            border-right-width: 0px;
                            border-bottom-width: 1px;
                            border-radius: 0;
                        }
                        // .el-form-item.is-error .el-input__inner,
                        // .el-form-item.is-error .el-input__inner:focus,
                        // .el-form-item.is-error .el-textarea__inner,
                        // .el-form-item.is-error .el-textarea__inner:focus {
                        //   border-color: #ff4949;
                        //   border: none;
                        //   border-bottom: 1px solid #E4E9F0;
                        //   border-radius: 0;
                        // }
                        // .el-form-item.is-success .el-input__inner,
                        // .el-form-item.is-success .el-input__inner:focus,
                        // .el-form-item.is-success .el-textarea__inner,
                        // .el-form-item.is-success .el-textarea__inner:focus {
                        //   border-color: #ff4949;
                        //   border: none;
                        //   border-bottom: 1px solid #E4E9F0;
                        //   border-radius: 0;
                        // }
                        // input:-internal-autofill-selected{
                        //   background-color: #fff !important;
                        //   border: none;
                        //   border-bottom: 1px solid #E4E9F0;
                        // }
                        // input .el-input__inner{
                        //   background-color: #fff !important;
                        // }
                    }
                    .password {
                        width: 100%;
                        display: flex;
                        justify-content: space-between;
                        font-size: 12px;
                        span {
                            color: #069ad5;
                            cursor: pointer;
                        }
                        ::v-deep {
                            .el-checkbox__label {
                                font-size: 12px;
                                // color: #212121;
                            }
                        }
                    }
                    .button {
                        width: 100%;
                        height: 36px;
                        // background-color: #0cb2fa;
                        border-radius: 2px;
                        color: #fff;
                        border: none;
                        font-size: 18px;
                        margin-bottom: 10px;
                        margin-top: 30px;
                    }
                    .login-text {
                        color: #212121;
                        height: 20px;
                        p {
                            position: absolute;
                            background-color: #fff;
                            height: 20px;
                            line-height: 20px;
                            margin-top: -10px;
                            width: 100px;
                            text-align: center;
                            margin-left: 70px;
                            font-size: 12px;
                        }
                        hr {
                            color: #dde2eb;
                        }
                    }
                    .other-login {
                        display: flex;
                        justify-content: space-evenly;
                        img {
                            cursor: pointer;
                        }
                    }
                }
            }
        }
    }
}

.company {
    // min-width: 700px;
